INSERT INTO `info_comment` VALUES (6, '2013-08-09 11:04:18', '2013-08-09 11:04:18', 'Hello anh, \n\nTheo ý kiến của anh , việc vô một trường không nằm trong ranking thì có tốt?\n\nCó cách nào tìm ngành thông qua một hệ thống bao gồm các trường có ngành đó không anh??\n\nEm cám ơn anh.', 'tantun_20193@yahoo.com.vn', 'Tusen', 1, 'cach-tim-hoc-bong-du-hoc-my-hieu-qua', 'khachhang', '', 0);
INSERT INTO `info_comment` VALUES (7, '2013-09-05 11:17:12', '2013-09-05 11:17:12', 'Nội dung khóa học rất phù hợp cho việc refresh kiến thức, giá cả phù hợp...Thank Stevbros.', 'tanhao.lee@gmail.com', 'Harry Lee', 1, 'gia-han-chung-chi-pmp-renew-pmp-cert-sau-3-nam-60-pdus-online-re-nhat', 'khachhang', '', 0);
INSERT INTO `info_comment` VALUES (8, '2013-12-08 00:14:28', '2013-12-08 00:14:28', 'I think there is a mistake in this post, plz note:\n* Contact Hours: Before the exam you need 35 Contact Hours\n* PDUs: After you become a certified PMP, you will need to accumulate 60 Professional Development Units (PDUs) every three years to keep your certification current.\nMay be contents of the training can be identical, but names are different due to objective reasons.', 'phamquochungxd@gmail.com', 'Andy Pham', 1, 'luyen-thi-pmp-online-luyen-thi-pmp-qua-mang-re-nhat', 'khachhang', '', 0);
