INSERT INTO `slider_banner` VALUES (1, 'Đào tạo Quản Lý Dự Án cho Doanh Nghiệp', 'slider.jpg', 'javascript:;', '<p>Chuyên đào tạo Quản Lý Dự Án theo tiêu chuẩn PMBOK, luyện thi PMP®, trí tuệ cảm xúc trong quản lý dự án, ... cho doanh nghiệp tại châu Á&nbsp;trong nhiều lĩnh vực khác nhau</p>\r\n', '2013-04-19 01:45:53', '2013-04-19 19:35:29', 1, NULL, 1, 'admin', 'phuong', 0);
INSERT INTO `slider_banner` VALUES (2, 'Tư Vấn Du Học Singapore/ Mỹ', 'slider2.jpg', 'javascript:;', '<p>Giải pháp du học đúng ngành học, đúng trường, đúng nơi ở, hổ trợ khi đang học, đáp ứng tất cả nhu cầu của từng bạn. Đạc biệt&nbsp;đảm bảo thành công visa du học Mỹ 100%.</p>\r\n', '2013-06-13 15:52:33', '2013-06-13 19:38:34', 1, NULL, 1, 'admin', 'phuong', 0);
