INSERT INTO `web_info` VALUES (102, 'BẾP CHÍNH BẾP Á NHÀ HÀNG', 'Bep-Chinh-Bep-A-Nha-Hang-102', 'bep-chinh-nha-hang.jpg', 'Chương trình đào tạo những Đầu bếp Chuyên nghiệp Bếp Á sẽ làm việc tại các Nhà hàng, Khách sạn...', '<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">1. Đặc điểm chương trình: </span>đây là Chương trình đào tạo những Đầu bếp Chuyên nghiệp sẽ làm việc tại các Nhà hàng, Khách sạn, Khu Du lịch có khả năng làm Bếp chính để chế biến các món ăn thông dụng và đặc sản cho Khách hàng.</p>\r\n\r\n<p>Tham gia Chương trình này, Học viên được học với các Bếp Trưởng hàng đầu tại Việt Nam, được thực tập và có cơ hội việc làm tại các Nhà hàng, Khách sạn, Khu Du Lịch lớn với công việc ổn định, môi trường làm việc chuyên nghiệp, thu nhập tốt.</p>\r\n\r\n<p><span style="color: rgb(255, 0, 0); ">2. <span style="font-weight: bold; ">Thời gian học</span>: </span>4 tháng</p>\r\n\r\n<p><span style="color: rgb(255, 0, 0); ">3. <span style="font-weight: bold; ">Chứng chỉ hoàn tất khóa học</span>: </span>do Cơ Sở Dạy Nghề Ẩm Thực NetSpace cấp có giá trị toàn quốc và Hội đầu bếp Chuyên nghiệp Sài Gòn chứng nhận.</p>\r\n\r\n<p><span style="color: rgb(255, 0, 0); ">4. <span style="font-weight: bold; ">Học phí:</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>12.000.000 đ/khóa (Đã bao gồm chi phí nguyên vật liệu thực hành và đồng phục bếp)</div>\r\n\r\n<div><span style="font-style: italic;">(Có thể đóng 2 lần, mỗi lần 6.000.000đ)</span></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">5. Nội dung khóa học</span></p>\r\n\r\n<p><span style="font-weight: bold; "><span style="font-style: italic; color: rgb(0, 0, 205); ">Phần 1: Các nguyên tắc bếp căn bản</span></span></p>\r\n\r\n<p>- Giới thiệu sơ nét về Ẩm thực Việt Nam</p>\r\n\r\n<p>- Những đặc trưng của Ẩm thực Việt Nam</p>\r\n\r\n<p>- Các nguyên tắc cơ bản trong kỹ thuật nấu ăn</p>\r\n\r\n<p>- Thói quen ăn uống của Người Việt</p>\r\n\r\n<p>- Các nguyên tắc cơ bản trong chọn lựa và bảo quản nguyên liệu và gia vị</p>\r\n\r\n<p>- Phương pháp cắt, thái các loại nguyên liệu</p>\r\n\r\n<p>- Nguyên tắc sử dụng nước chấm</p>\r\n\r\n<p>- Nguyên tắc trang trí món ăn</p>\r\n\r\n<p>- Những yếu tố quyết định sự thành công trong nghề bếp</p>\r\n\r\n<p><span style="font-weight: bold; "><span style="font-style: italic; color: rgb(0, 0, 205); ">Phần 2: Cách chế biến các món ăn</span></span></p>\r\n\r\n<p>- Các món khai vị</p>\r\n\r\n<p>- Các món trộn</p>\r\n\r\n<p>- Các món heo</p>\r\n\r\n<p>- Các món bò</p>\r\n\r\n<p>- Các món gà</p>\r\n\r\n<p>- Các món hải sản</p>\r\n\r\n<p>- Các món đặc sản</p>\r\n\r\n<p>- Các món điểm tâm sáng</p>\r\n\r\n<p>- Các món tráng miệng</p>\r\n\r\n<p><span style="font-weight: bold; "><span style="font-style: italic; color: rgb(0, 0, 205); ">Phần 3: Đi thực tập thực tế tại Bếp các Nhà hàng, Khách sạn</span></span></p>\r\n\r\n<p><span style="font-weight: bold; "><span style="font-style: italic; color: rgb(0, 0, 205); ">Phần 4: Thi, kiểm tra tốt nghiệp</span></span></p>\r\n', '2012-03-16 00:00:00', '2012-03-16 08:15:31', 0, 'BẾP CHÍNH BẾP Á NHÀ HÀNG', 0, 0, 'vi', ',31,56,', 'admin', 'admin', 0);
INSERT INTO `web_info` VALUES (103, 'Quản lý bếp - Bếp trưởng', 'quan-ly-bep-bep-truong-103', 'Bep-Truong-Nha-Hang.jpg', 'Để trở thành Bếp Trưởng Nhà Hàng, Bạn phải qua các lớp huấn luyện về bếp và có kinh nghiệm làm việc với các vị trí Phụ bếp, Bếp chính (Chảo).', '<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">1. Đặc điểm chương trình:</span></p>\r\n\r\n<h2 style="text-align: justify;">Để trở thành Bếp Trưởng Nhà Hàng, Bạn phải qua các lớp huấn luyện về bếp và có kinh nghiệm làm việc với các vị trí Phụ bếp, Bếp chính (Chảo).</h2>\r\n\r\n<p style="text-align: justify;">Bếp trưởng là Người chỉ huy toàn bộ các nhân sự và hoạt động bếp, do đó đòi hỏi Bạn phải được đào tạo các kỹ năng về tổ chức bếp, kỹ năng lãnh đạo đội ngũ bếp, ngoài ra Bạn là Người đưa ra các thực đơn, món ăn độc đáo, giúp Nhà hàng thành công.</p>\r\n\r\n<p style="text-align: justify;">Cơ Sở Dạy Nghề Ẩm Thực&nbsp;NetSpace đã xây dựng Chương trình đào tạo Bếp Trưởng Nhà hàng dành cho Các bạn đã qua các khóa huấn luyện về Phụ bếp, Bếp chính hoặc có kinh nghiệm làm việc trên 1 năm trong nghề bếp .</p>\r\n\r\n<p style="text-align: justify;">Tham gia Chương trình này, Học viên được học với các Bếp Trưởng, các Chuyên Gia Ẩm Thực hàng đầu Việt Nam, được thực tập và có cơ hội thăng tiến hoặc cơ hội việc làm tại các Nhà hàng, Khách sạn, Khu Du Lịch lớn.</p>\r\n\r\n<p><span style="color: rgb(255, 0, 0); "><span style="font-weight: bold; ">2. Thời gian học</span>:</span> 1 tháng</p>\r\n\r\n<p><span style="font-weight: bold; "><span style="color: rgb(255, 0, 0); ">3. Chứng chỉ hoàn tất khóa học:</span></span> do Cơ Sở Dạy Nghề Ẩm Thực NetSpace cấp có giá trị toàn quốc và Hội đầu bếp Chuyên nghiệp Sài Gòn chứng nhận.</p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">4. Học phí:</span></p>\r\n\r\n<p><span style="line-height: 1.6em;">10.000.000 đ/khóa</span></p>\r\n\r\n<div><span style="color: rgb(255, 0, 0); font-weight: bold; line-height: 1.6em;">5. Nội dung khóa học</span></div>\r\n\r\n<p>- Quản lý vệ sinh, an toàn thực phẩm, phương pháp bảo quản nguyên liệu &nbsp;</p>\r\n\r\n<p>- An toàn phòng cháy, chữa cháy, xử lý nước thải</p>\r\n\r\n<p>- Thiết kế bếp và dụng cụ bếp Nhà hàng</p>\r\n\r\n<p>- Marketing bếp</p>\r\n\r\n<p>- Mô tả công việc và bố trí nhân sự bếp</p>\r\n\r\n<p>- Quy trình hoạt động bếp</p>\r\n\r\n<p>- Quản lý các mối quan hệ tại Nhà hàng</p>\r\n\r\n<p>- Tổ chức và quản lý tiệc</p>\r\n\r\n<p>- Thiết kế thực đơn, định mức, định lượng món ăn&nbsp;</p>\r\n\r\n<p style="text-align: center;">&nbsp;</p>\r\n', '2013-01-14 00:00:00', '2013-01-14 10:59:24', 1, 'Bep Chinh Nang Cao Bep Truong,Bếp chính nâng cao, Bếp trưởng', 0, 1, 'vi', ',3,22,209,65,', 'admin', 'admin', 0);
INSERT INTO `web_info` VALUES (330, 'Đào tạo phụ bếp miễn phí và cơ hội việc làm 2013', 'dao-tao-phu-bep-mien-phi-va-co-hoi-viec-lam-2013-330', 'truong day nau an tai can tho(2).jpg', 'Cơ sở dạy nghề ẩm thực NetSpace phối hợp với Công Ty TNHH Vedan và Hội Đầu Bếp Chuyên Nghiệp Sài Gòn tổ chức các khóa đào tạo nghề bếp miễn phí cho các bạn trẻ có hoàn cảnh khó khăn và đam mê ẩm thực. Sau khi hoàn thành khóa học, Học viên sẽ được bố trí việc làm tại các nhà hàng, khách sạn. ', '<h2 style="text-align: justify;">Trường&nbsp;dạy nghề ẩm thực NetSpace phối hợp với Công Ty TNHH Vedan và Hội Đầu Bếp Chuyên Nghiệp Sài Gòn tổ chức các khóa đào tạo nghề bếp miễn phí cho các bạn trẻ có hoàn cảnh khó khăn và đam mê ẩm thực. Sau khi hoàn thành khóa học, Học viên sẽ được bố trí việc làm tại các nhà hàng, khách sạn.&nbsp;</h2>\r\n\r\n<p style="text-align: justify;">Các bạn trẻ yêu thích nghề bếp, có hoàn cảnh khó khăn có thể đăng ký tham gia Chương trình đào tạo phụ&nbsp;bếp miễn phí từ ngày 1/11/2013 đến 15/11/2013.<br />\r\nSau khi nộp đầy đủ hồ sơ và vượt qua vòng phỏng vấn tại Trường dạy nghề ẩm thực&nbsp;NetSpace, các bạn sẽ được tham gia khóa đào tạo nghề bếp miễn phí. Tham gia khóa học các bạn sẽ được trang bị các kỹ năng và kiến thức khi chế biến món ăn. Các Giảng viên có chuyên môn cao và kinh nghiệm lâu năm trong nghề sẽ phụ trách giảng dạy chương trình này.<br />\r\nĐại diện Trường&nbsp;dạy nghề ẩm thực NetSpace cho biết, chương trình đào tạo này đã được triển khai từ năm 2012 và có nhiều bạn trẻ tham gia và thành công sau khi hoàn thành khóa học.&nbsp;<br />\r\nVới mong muốn cung ứng nguồn nhân lực bếp có chất lượng cao trong ngành ẩm thực, Trường&nbsp;NetSpace không ngừng nỗ lực nâng cao chất lượng giảng dạy, cập nhật chương trình đào tạo phù hợp với nhu cầu xã hội, trang bị tốt các phòng học và trang thiết bị thực hành, tập hợp Đội ngũ giảng viên có tay nghề cao và tâm huyết với sự nghiệp giáo dục. Ngoài ra, NetSpace luôn gắn các hoạt động của Trường với các hoạt động xã hội, hỗ trợ các bạn trẻ có hoàn cảnh khó khăn được theo đuổi niềm đam mê nghề bếp của mình. Đây chính là những yếu tố tạo nên uy tín của Trường&nbsp;dạy nghề ẩm thực NetSpace trong những năm qua. &nbsp;</p>\r\n\r\n<p style="text-align: justify;"><a href="http://www.dayamthuc.vn/xem-tin/thong-bao-chuong-trinh-dao-tao-phu-bep-mien-phi-2013-329.html"><span style="color:#0000FF;"><strong>Tham khảo chi tiết khóa đào đạo phụ bếp miễn phí&nbsp;</strong></span></a>&nbsp; &nbsp;</p>\r\n\r\n<p style="text-align: justify;">Các bạn muốn tham dự khóa đào tạo này có thể:<br />\r\n<u>Đăng ký trực tiếp tại:&nbsp;</u><br />\r\n<strong>Trường&nbsp;dạy nghề ẩm thực NetSpace</strong>&nbsp;<br />\r\nĐịa chỉ: 30 Nguyễn Huy Tự, phường Đa Kao, quận 1, thành phố Hồ Chí Minh<br />\r\nĐiện thoại: (08)6291 2698 , Hotline: 0987 937 938<br />\r\nHoặc <u>đăng ký trực tuyến tại:</u>&nbsp;<a href="http://www.dayamthuc.vn/Dang-Ky-Truc-Tuyen/"><span style="color:#0000CD;">http://www.dayamthuc.vn/Dang-Ky-Truc-Tuyen/</span></a></p>\r\n\r\n<p style="text-align: justify;"><em>* Đối với những bạn đăng ký trực tuyến cần nộp đầy đủ hồ sơ tại Trường trước ngày 15/11/2013</em></p>\r\n\r\n<p style="text-align: justify;"><em><strong>(Ưu tiên những hồ sơ nộp trực tiếp)</strong></em></p>\r\n\r\n<p style="text-align: center;">&nbsp;&nbsp;<img alt="" height="95" src="/upload/images/articles/logo.png" width="404" /></p>\r\n\r\n<p>&nbsp;</p>\r\n', '2013-10-29 10:31:23', '2013-10-29 14:41:56', 1, 'đào tạo phụ bếp, dao tao phu bep', 0, 0, 'vi', ',6,56,', 'huyen', 'chau', 0);
