INSERT INTO `web_info` VALUES (105, 'Quản lý nhà hàng', 'Quan-Ly-Nha-Hang-105', 'quan-ly-nha-hang.jpg', 'Chương trình này dành cho các bạn mong muốn làm việc với vị trí Quản lý Nhà hàng...', '<p style="text-align: center;"><span style="text-align: start; font-weight: bold;">Tần suất khai giảng:</span><span style="text-align: start;">&nbsp;</span><span style="text-align: start; font-weight: bold; color: rgb(255, 20, 147);">Hàng tháng</span></p>\r\n\r\n<p style="text-align: center;"><a href="http://www.dayamthuc.vn/dang-ky-truc-tuyen.html"><img align="" alt="đăng ký trực tuyến" border="0px" src="http://www.dayamthuc.vn/upload/tin-tuc/dang-ky-truc-tuyen.gif" /></a><br />\r\n&nbsp;</p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">1. Đặc điểm chương trình:</span>Chương trình này dành cho các bạn mong muốn làm việc với vị trí Quản lý Nhà hàng. Chương trình sẽ đào tạo các bạn có kiến thức và kỹ năng quản lý chuyên nghiệp Nhà hàng.</p>\r\n\r\n<p>Tham gia Chương trình này, Học viên được học với các Giảng viên là những Quản lý, Bếp Trưởng, Chuyên Gia Ẩm Thực hàng đầu Việt Nam, được thực tập và có cơ hội thăng việc làm tại các Nhà hàng, Khách sạn, Khu Du Lịch .</p>\r\n\r\n<p><span style="color: rgb(255, 0, 0); ">2. <span style="font-weight: bold; ">Thời gian học</span>: </span>4 tháng</p>\r\n\r\n<p><span style="color: rgb(255, 0, 0); ">3. <span style="font-weight: bold; ">Chứng chỉ hoàn tất khóa học</span>: </span>do Cơ Sở Dạy Nghề Ẩm Thực NetSpace cấp có giá trị toàn quốc và Hội đầu bếp Chuyên nghiệp Sài Gòn chứng nhận.</p>\r\n\r\n<p><span style="color: rgb(255, 0, 0); ">4. <span style="font-weight: bold; ">Học phí:&nbsp;</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>12.000.000 đ/khóa (Đã bao gồm chi phí nguyên vật liệu thực hành và đồng phục bếp)</div>\r\n\r\n<div><span style="font-style: italic;">(Có thể đóng 2 lần, mỗi lần 6.000.000đ)</span></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">5. Nội dung khóa học</span></p>\r\n\r\n<p><span style="font-weight: bold; "><span style="font-style: italic; ">Phần 1</span></span><span style="font-weight: bold; "><span lang="VI" style="font-style: italic; ">:</span></span><span style="font-weight: bold; "><span style="font-style: italic; "> Lý thuyết</span></span></p>\r\n\r\n<p>- Mô tả công việc và bố trí nhân sự Nhà hàng</p>\r\n\r\n<p>- Quản lý mối quan hệ giữa các bộ phận</p>\r\n\r\n<p>- Kiến thức căn bản về Bếp Nhà hàng</p>\r\n\r\n<p>-<span lang="VI"> Marketing Nhà hàng</span></p>\r\n\r\n<p>- Kỹ năng giao tiếp trong Nhà hàng</p>\r\n\r\n<p><span lang="VI">- Chăm sóc khách hàng</span></p>\r\n\r\n<p><span lang="VI">- Quản lý pha chế - phục vụ</span></p>\r\n\r\n<p><span lang="VI">- Kiểm soát &amp; quản lý tài chính Nhà hàng</span></p>\r\n\r\n<p><span lang="VI">- An toàn, vệ sinh thực phẩm</span></p>\r\n\r\n<p><span lang="VI">- Tiếng Anh trong Nhà hàng</span></p>\r\n\r\n<p><span style="font-weight: bold; "><span style="font-style: italic; ">Phần 2: Thực tập thực tế tại các Nhà hàng, Khách sạn, Khu Du Lịch</span></span></p>\r\n\r\n<p><span style="font-weight: bold; "><span style="font-style: italic; ">Phần 3: Thi, kiểm tra tốt nghiệp</span></span></p>\r\n\r\n<p style="text-align: center;"><a href="http://www.dayamthuc.vn/dang-ky-truc-tuyen.html"><img align="" alt="đăng ký trực tuyến" border="0px" src="http://www.dayamthuc.vn/upload/tin-tuc/dang-ky-truc-tuyen.gif" /></a></p>\r\n', '2013-01-13 00:00:00', '2013-01-13 08:40:44', 0, 'Quan Ly, Nha Hang', 0, 0, 'vi', ',31,56,', 'admin', 'admin', 0);
INSERT INTO `web_info` VALUES (106, 'CHƯƠNG TRÌNH ĐÀO TẠO NẤU ĂN GIA ĐÌNH', 'Chuong-Trinh-Dao-Tao-Nau-An-Gia-Dinh-106', 'nau-an-gia-dinh.jpg', 'Chứng chỉ hoàn tất khóa học: do Cơ Sở Dạy Nghề Ẩm Thực NetSpace cấp, có giá trị toàn quốc', '<p><span style="color: rgb(255, 0, 0); ">1. <span style="font-weight: bold; ">Thời gian học</span>:</span> 1 tháng</p>\r\n\r\n<p><span style="color: rgb(255, 0, 0); ">2. <span style="font-weight: bold; ">Học viên lựa chọn 1 trong các lớp:</span></span></p>\r\n\r\n<p>- Điểm tâm sáng</p>\r\n\r\n<p>- Nấu ăn gia đình căn bản</p>\r\n\r\n<p>- Nấu ăn gia đình nâng cao</p>\r\n\r\n<p><span style="color: rgb(255, 0, 0); ">3. <span style="font-weight: bold; ">Chứng chỉ hoàn tất khóa học</span>:&nbsp;</span><span style="font-weight: bold; ">do Cơ Sở Dạy Nghề Ẩm Thực NetSpace cấp, có giá trị toàn quốc và Hội đầu bếp chuyên nghiệp Sài Gòn chứng nhận.</span></p>\r\n\r\n<p><span style="color: rgb(255, 0, 0); ">4. <span style="font-weight: bold; ">Học phí:</span></span></p>\r\n\r\n<p>- Điểm tâm sáng: 2.000.000 đ/khóa</p>\r\n\r\n<p>- Nấu ăn gia đình căn bản: 3.000.000 đ/khóa</p>\r\n\r\n<p>- Nấu ăn gia đình nâng cao: 3.000.000 đ/khóa</p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">5. Nội dung khóa học:</span></p>\r\n\r\n<p><span style="font-weight: bold; ">1. Lớp điểm tâm sáng:</span></p>\r\n\r\n<p>- Cách lựa chọn nguyên vật liệu nấu các món điểm tâm sáng;</p>\r\n\r\n<p>- Cách chế biến các món ăn sáng thông dụng: Phở bò, Bún Bò Huế, Bún riêu, Hủ Tiếu Nam Vang;</p>\r\n\r\n<p>- Cách múc và trang trí các món điểm tâm sáng</p>\r\n\r\n<p><span style="font-weight: bold; ">2. Lớp nấu ăn gia đình căn bản:</span></p>\r\n\r\n<p>Tập trung các món ăn căn bản cho bữa cơm trưa, cơm chiều tại gia đình:</p>\r\n\r\n<p>- Cách chế biến các món kho: cá kho, thịt kho,…</p>\r\n\r\n<p>- Cách chế biến các món hấp: cá hấp,…</p>\r\n\r\n<p>- Cách làm các món chưng: mắm chưng,…</p>\r\n\r\n<p>- Cách làm các món xào: mực xào ngọt, rau xào,…</p>\r\n\r\n<p>- Cách thực hiện các món chiên: thịt chiên, cá chiên, mực dồn thịt,…</p>\r\n\r\n<p>- Cách làm các món canh: canh chua, canh rau, canh khổ qua,…</p>\r\n\r\n<p><span style="font-weight: bold; ">3. Lớp nấu ăn gia đình nâng cao:</span></p>\r\n\r\n<p>Tập trung đào tạo các món ăn dành cho gia đình trong các dịp lễ, cuối tuần,…</p>\r\n\r\n<p>- Cách thực hiện các món từ bò: bò sốt vang, bò nấu la-gu…</p>\r\n\r\n<p>- Cách làm các món heo: sườn nướng, giò heo muối chiên giòn…</p>\r\n\r\n<p>- Chế biến các món gà: gà nướng, cà ri gà…</p>\r\n\r\n<p>- Cách làm món dê: dê hấp, lẩu dê…</p>\r\n\r\n<p>- Các món hải sản: cá chưng tương, cá sốt cam, cá nướng, chả cua, lẩu hải sản</p>\r\n', '2012-03-16 00:00:00', '2012-03-16 10:40:35', 0, 'NẤU ĂN GIA ĐÌNH', 0, 0, 'vi', ',31,56,', 'admin', 'admin', 0);
INSERT INTO `web_info` VALUES (107, 'KHÁC BIỆT CỦA CHƯƠNG TRÌNH', 'Khac-Biet-Cua-Chuong-Trinh-107', 'khac-biet-cua-chuong-trinh-1.jpg', 'KHÁC BIỆT CỦA CHƯƠNG TRÌNH', '\r\n<div style="text-align: center;"><br />\r\n	</div>\r\n<div style="text-align: center;">\r\n	<img src="/upload/tin-tuc/khac-biet-cua-chuong-trinh.jpg" alt="Chú trọng thực hành - KHÁC BIỆT CỦA CHƯƠNG TRÌNH" align="" style="" width="595" height="2211" border="0" /></div> ', '2012-03-16 00:00:00', '2012-03-16 00:00:00', 1, 'Khac Biet, Cua Chuong Trinh', 0, 0, 'vi', ',13,56,', 'admin', '', 0);
INSERT INTO `web_info` VALUES (181, 'Nghề bếp và tương lai tươi sáng - Dạy nấu ăn', 'Nghe-Bep-Va-Tuong-Lai-Tuoi-Sang-Day-Nau-An-181', 'Nghe-Bep-Va-Tuong-Lai-Tuoi-Sangday-Nau-An.jpg', 'Nhu cầu tuyển dụng nhân lực của nghề bếp luôn luôn tăng mạnh cùng với sự phát triển nhanh chóng', '\r\n<p><span style="font-weight: bold;"><span style="font-style: italic;">Nhu cầu tuyển dụng nhân lực của nghề bếp luôn luôn tăng mạnh cùng với sự phát triển nhanh chóng của hệ thống nhà hàng, quán ăn, khách sạn trên khắp cả nước. Vì vậy, nghề bếp đang trở thành một nghề đắt giá và nhu cầu đào tạo, dạy nấu ăn là rất lớn.</span></span></p>\r\n\r\n\r\n<p align="center">\r\n	\r\n	\r\n	<img src="/upload/tin-tuc/Nghe-Bep-Va-Tuong-Lai-Tuoi-Sangday-Nau-An.jpg" alt="Học viên Trường Dạy Nghề Ẩm Thực NetSpace đang học nấu ăn" align="" style="" width="550" height="368" border="0" /></p>\r\n\r\n\r\n<p align="center"><span style="font-style: italic;">Học viên Trường Dạy Nghề Ẩm Thực NetSpace đang học nấu ăn</span></p>\r\n\r\n\r\n<p>Tìm hiểu của phóng viên chúng tôi tại trường Dạy nghề Ẩm Thực NetSpace cho thấy số lượng Học viên tốt nghiệp nghề nấu ăn chỉ trong thời gian 4 tháng đã có việc làm ngay hoặc đã tự tổ chức kinh doanh quán ăn vừa và nhỏ cho bản thân.</p>\r\n\r\n\r\n<p>Theo Hiệu Trưởng Trường Dạy nghề Ẩm Thực NetSpace- Thạc sỹ Nguyễn Quốc Y chia sẻ ngoài những kiến thức và kỹ năng chuyên môn nghề bếp, Học viên còn được trang bị những kỹ năng mềm như: kỹ năng giao tiếp, kỹ năng làm việc nhóm, kỹ năng vượt khó…, ngoài các buổi học nấu ăn tại Trường, các bạn Học viên được tham gia các Chương trình ngoại khóa, các Chương trình giao lưu với các Đầu bếp nổi tiếng, các Siêu đầu bếp, Vua bếp … điều đó tạo ra những khác biệt lớn đối với các <a href="http://www.dayamthuc.vn/danh-muc/Chuong-Trinh-Dao-Tao-3-1.html"><span style="font-weight: bold;">Chương trình dạy nấu ăn của Trường</span></a>.</p>\r\n\r\n\r\n<p>Sinh viên Ngọc Long cho biết sau khi tốt nghiệp khóa học 4 tháng Bếp Việt Nam tại Trường NetSpace đã được nhận làm Bếp tại Nhà hàng Hồng Cường với mức lương 5 triệu/tháng, nhiều Học viên sau khi tốt nghiệp ngành Bếp sau vài năm đã trở thành Bếp Trưởng các Nhà hàng lớn với thu nhập trên 10 triệu/tháng.</p>\r\n\r\n\r\n<p>Tìm hiểu từ thầy Lê Võ Anh Duy, Trưởng bộ môn Bếp Việt cho biết: “Các lớp Bếp chính Nhà hàng luôn thu hút đông đảo Các bạn trẻ theo học, bởi vì sau khi kết thúc khóa học, Các bạn có thể tự tin làm việc tại các Nhà hàng lớn.&nbsp;</p>\r\n\r\n\r\n<p style="text-align: center;">\r\n	\r\n	\r\n	<img src="http://www.dayamthuc.vn/upload/tin-tuc/Nghe-Bep-Va-Tuong-Lai-Tuoi-Sangday-Nau-An-2.jpg" alt="Nghề bếp và tương lai tươi sáng-Dạy nấu ăn" align="" width="550" height="368" style="" border="0" /></p>\r\n\r\n\r\n<p>Như vua bếp Trần Văn Nghĩa đã tâm sự: “Nếu trở lại từ đầu, để chọn nghề, tôi vẫn chọn nghề đầu bếp. Tuy nó có nhọc nhằn nhưng ở đây tôi có nhiều niềm vui trong sự sáng tạo và nhờ nó, tôi đã mang lại cho biết bao nhiêu người niềm hân hoan thưởng thức hương vị cuộc sống, đó là điều quý nhất mà nghề bếp đã cống hiến”.Tiềm năng nghề Bếp theo ý kiến của các “Vua” Bếp</p> Còn theo ông Lý Sanh, Hội đầu bếp chuyên nghiệp TP.HCM: gần 20 năm nay, nguồn nhân lực đầu bếp trong nước được đào tạo và trưởng thành từ hai nguồn là truyền nghề và trường dạy nấu ăn làm bếp. Đa số món ăn Việt Nam lâu nay được lưu truyền qua truyền nghề, ít có tài liệu ghi ch&eacute;p công thức, cách làm cụ thể. Chính vì thế, nhiều món ăn truyền thống được truyền thụ cảm tính pha với sáng tạo của địa phương hơn là mang tính kinh điển truyền thống (so với món Âu), chưa thống nhất được tên gọi. Từ đó cho thấy, việc đào tạo đầu bếp chuyên nghiệp là đòi hỏi mang tính cấp bách để kịp với sự phát triển nhanh chóng của ngành du lịch, cũng như cần phải chuẩn hóa tay nghề chuyên môn cho đầu bếp thông qua tuyển sinh và giảng dạy; xác định nguồn gốc món ăn, chuẩn hóa món ăn.', '2013-03-08 00:00:00', '2013-03-08 00:00:00', 1, 'Nghe Bep, Va Tuong Lai Tuoi Sang, day Nau An', 0, 0, 'vi', ',6,56,', 'admin', '', 0);
INSERT INTO `web_info` VALUES (108, 'Văn Hóa Ẩm Thực Đông Tây', 'Van-Hoa-Am-Thuc-Dong-Tay-108', 'Van-Hoa-Am-Thuc-Dong-Tay.jpg', 'Trong thực đơn của cư dân nước này sẽ có những điểm khác biệt với cư dân nước khác.', '\r\n<p style="text-align: justify;">\r\n	\r\n	<img src="/upload/tin-tuc//Van-Hoa-Am-Thuc-Dong-Tay.jpg" alt="" align="left" border="0px" style="margin-right: 5px; " /><span style="font-style: italic; ">Trong\r\nthực đơn của cư dân nước này sẽ có những điểm khác biệt với cư dân\r\nnước khác. Vì sao lại tồn tại sự khác biệt này? Dường như bất cứ\r\nmón ăn nào, từ châu chấu rang, cà cuống nướng đến thịt chuột, nội\r\ntạng động vật như: lòng, gan, tim... đều có thể tìm thấy trong thực\r\nđơn của người Việt. Những thức ăn này rất phổ biến, nhiều món trở\r\nthành đặc sản.</span> </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Tuy\r\nnhiên, các loại thực phẩm trên lại bị cấm kỵ ở một số nơi trên thế\r\ngiới. Tại sao? Một số lý giải sau sẽ giúp bạn tránh mắc phải sai\r\nlầm trong ăn uống khi có dịp đến một số nơi.</p>\r\n\r\n\r\n\r\n<p style="text-align: justify;"><span style="font-weight: bold; ">Động\r\nvật bò sát và lưỡng cư (cá sấu, rắn, ếch, thằn lằn...) </span></p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Thịt\r\ntừ các loại động vật này được dùng để chế biến món ăn ở nhiều\r\nnước châu Âu và châu Á. Ngoài ra, thịt ếch, đặc biệt là đùi ếch,\r\nđược người Pháp, Bồ Đào Nha, Indonesia, Trung Quốc, vùng Caribbean và\r\nmột số nơi tại Mỹ ưa chuộng. Tuy nhiên, với người Do Thái hoặc Hồi\r\ngiáo, các loại thịt từ động vật lưỡng cư như: ếch, cá sấu và rắn\r\nlà cấm kỵ. </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;"><span style="font-weight: bold; ">Thịt\r\ntừ động vật lông vũ</span> </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Các\r\nloại thịt gia cầm như gà, gà tây, vịt được dùng để chế biến món ăn\r\nở đa số các nước trên thế giới. Tuy nhiên, có một số giới hạn trong\r\nhạng mục thịt từ các loài lông vũ như: ở châu Âu và Mỹ, nhiều người\r\nkhông ăn thịt thiên nga (ngỗng trời). Ở Anh, thiên nga trắng được bảo\r\nvệ vì người ta cho rằng đó là loài vật thuộc về nữ hoàng. Ngoài\r\nra, hầu hết mọi người ở các nước phương Tây xem các loài chim hót\r\nđều là động vật hoang dã nên không ăn thịt. </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;"><span style="font-weight: bold; ">Thịt\r\nmèo</span> </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Được\r\ndùng ở miền Nam Trung Quốc và Việt Nam. Nhiều người cho rằng thịt\r\nmèo có tính nhiệt, rất tốt khi dùng làm thực phẩm ăn vào mùa đông.\r\nNgười ta sử dụng luôn một số cơ quan nội tạng của mèo như: bao tử,\r\nruột, còn đầu và các phần khác thì vứt đi. Ở Hàn Quốc, thịt mèo\r\nthường dành cho người mắc bệnh khớp và đau dây thần kinh. Ngoài ra,\r\nthịt mèo còn phổ biến trong thực đơn của người Peru. Trong dịp lễ\r\nhội diễn ra vào tháng Chín hàng năm ở La Quebrada, nhiều người tham\r\ngia biểu diễn tài nghệ chế biến món ăn với thịt mèo. Người dân ở\r\nmột số vùng nông thôn của Thụy Sỹ và miền Bắc nước Ý cũng ăn thịt\r\nmèo. </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;"><span style="font-weight: bold; ">Thịt\r\ncày (thịt chó)</span> </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Tuy\r\nchẳng có luật nào cấm ăn thịt chó ở Mỹ và các nước châu Âu, nhưng\r\nloại thực phẩm này không được nhiều người chấp nhận. Có lẽ do suy\r\nnghĩ chó là loài vật rất trung thành với người, lại là con thân\r\nthiết nên người ta không nỡ ăn thịt chúng. Ở Việt Nam, thịt chó khá\r\nphổ biến và được nhiều người ưa thích, đặc biệt là "tín\r\nđồ" của các quán nhậu. Ở Hàn Quốc, nhiều quý ông rất ưa món\r\nthịt chó nấu súp vì tin rằng nó tốt cho sức khỏe. </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;"><span style="font-weight: bold; ">Thịt\r\nchuột</span> </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Đa\r\nsố người phương Tây xem chuột là loài vật dơ bẩn, do đó thịt của\r\nchúng không thích hợp để làm món ăn. Tuy nhiên, thịt chuột đồng lại\r\nđược rất nhiều người ở vùng nông thôn Thái Lan, Việt Nam và một số\r\nvùng ở Đông Dương ưa thích. Thịt chuột mía và chuột đồng rất giàu\r\nprotein và trở thành loại thực phẩm phổ biến ở châu Phi.</p>\r\n\r\n\r\n\r\n<p style="text-align: justify;"><span style="font-weight: bold; ">Ốc\r\nsên</span> </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Là\r\nloài nhuyễn thể mà con người có thể ăn được. Ở Trung Quốc và nhiều\r\nnước châu Á cũng như Pháp và các nước thuộc vùng Địa Trung Hải, có\r\nrất nhiều món ăn chế biến từ thịt ốc sên. Tuy nhiên nếu ăn thịt ốc\r\nsên ở Anh, Ireland và Mỹ, bạn sẽ bị người khác nhìn bằng ánh mắt\r\nkhác thường. Họ xem ăn ốc sên là hành động kinh khủng.</p>\r\n\r\n\r\n\r\n<p style="text-align: justify;"><span style="font-weight: bold; ">Thịt\r\nlợn</span> </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Người\r\nHồi giáo, Do Thái không ăn thịt lợn. Người dân ở vùng Trung Đông cho\r\nrằng lợn là loài vật không sạch sẽ và ăn tạp.</p>\r\n\r\n\r\n\r\n<p>&nbsp;</p>\r\n\r\n\r\n\r\n<p style="text-align: justify;"><span style="font-weight: bold; ">Thịt\r\nbò</span> </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Đây\r\nlà loại thịt khá phổ biến ở nhiều nước trên thế giới và đứng sau\r\nthịt lợn. Nhiều người ở Anh, Ireland, Mỹ và Canada không ăn thịt bê\r\n(bò con) vì lo ngại bị chỉ trích đối xử thô bạo với súc vật. Ở Ấn\r\nĐộ, thịt bò là thực phẩm cấm kỵ, đặc biệt đối với những người\r\ntheo Đạo Hindu, bởi vì bò được xem là linh vật.</p>\r\n\r\n\r\n\r\n<p style="text-align: justify;"><span style="font-weight: bold; ">Thịt\r\nngựa</span> </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Được\r\ndùng phổ biến trong thực đơn của những người sống ở vùng Scandinavia\r\n(bao gồm Na-uy, Đan Mạch và Thụy Điển ở Bắc Âu) và một số vùng ở\r\nchâu Âu. Tuy nhiên, ở các nước Anh, Mỹ và Úc, việc cung cấp loại thịt\r\nnày bị xem là bất hợp pháp. Riêng ở Nhật Bản, thịt ngựa được xem\r\nnhư món đặc sản và ăn sống giống sashimi.</p>\r\n\r\n\r\n\r\n<p style="text-align: justify;"><span style="font-weight: bold; ">Côn\r\ntrùng</span> </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Châu\r\nchấu rang giòn được xem là món ăn bình dị ở một số vùng quê Việt\r\nNam. Ngoài ra, nhiều loại côn trùng khác như: dế, trứng kiến, ong,\r\nnhộng, nhện... cũng được chế biến thành món ăn ở các nước châu Á\r\nnhư: Lào, Thái Lan, Campuchia, Nhật... Người dân Nam Phi dùng chung côn\r\ntrùng với cháo ngô. Người châu Âu rất hiếm khi ăn côn trùng vì xem đó\r\nlà loài phá hoại mùa màng chứ không phải nguồn thực phẩm.<span style="font-weight: bold; "></span><span style="font-weight: bold; "></span></p>\r\n\r\n\r\n\r\n<p style="text-align: justify;"><span style="font-weight: bold; ">Nội tạng </span></p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Nội\r\ntạng của gia súc vẫn được nhiều người châu Âu và châu Á làm sạch và\r\nchế biến thành món ăn. Nhưng ở Úc, Canada và Mỹ, nhiều người không ăn\r\nđược các món này vì sợ. Các loại lòng bò, lòng lợn chỉ dùng để\r\nchế biến thức ăn dành cho vật nuôi trong nhà. </p>\r\n\r\n\r\n\r\n<p style="text-align: justify;">Tiết\r\nđộng vật (như tiết lợn, gà, vịt...). Rất phổ biến ở Trung Quốc,\r\nViệt Nam, Thái Lan... Tại Philippines, món ăn truyền thống dinuguan được\r\nlàm từ tiết lợn ăn với bánh bột gạo. Ở châu Âu, đặc biệt là Phần\r\nLan, một số nước thuộc vùng Baltic như: Ba Lan, Latvia, tiết canh cũng\r\nrất phổ biến. Tuy nhiên, người Mỹ không ăn được tiết canh.</p>\r\n\r\n\r\n\r\n<p align="right" style="text-align: right;"><span style="font-weight: bold; "><span style="font-style: italic; ">Theo TTGĐ\r\n&amp; Vào bếp</span></span></p>', '2012-03-17 00:00:00', '2012-03-17 00:00:00', 1, 'Văn Hóa Ẩm Thực Đông Tây', 0, 0, 'vi', ',6,56,', 'admin', '', 0);
INSERT INTO `web_info` VALUES (109, 'Ai viết lịch sử phở? ', 'Ai-Viet-Lich-Su-Pho-109', 'Ai-Viet-Lich-Su-Pho.jpg', 'Nước Pháp đã nổi tiếng thế giới về bánh Tây không khác gì tiếng tăm về cuộc cách mạng đòi dân quyền và nhân quyền năm 1789', '\r\n\r\n<p style="text-align: justify;">\r\n	<p>\r\n		<img src="/upload/tin-tuc//Ai-Viet-Lich-Su-Pho.jpg" alt="" align="left" border="0px" style="margin-right: 5px; " /><span style="font-weight: bold; ">Nguyễn Tuân, Vũ Bằng không viết lịch sử<br />\r\n			\r\n</span>Nước Pháp đã nổi tiếng thế giới về bánh Tây không khác gì tiếng tăm về cuộc\r\ncách mạng đòi dân quyền và nhân quyền năm 1789. Nhưng dân tộc Việt Nam bây giờ\r\ncũng nổi tiếng về món Phở đã được truyền bá khắp thế giới. <br />\r\n		\r\n<br />\r\n		\r\nNhưng chúng ta biết gì về Phở? Rất ít. Ngay cả cái tên, tại sao lại gọi món này\r\nlà Phở, cũng còn mù mờ với nhiều giả thuyết khác nhau. Có người dám nói rằng\r\ncái tên Phở gốc tiếng Tây; có người nói gốc tiếng Tàu; trong lúc cha sinh mẹ đẻ\r\nra món ăn này là người Việt Nam rõ ràng. Cũng may chưa ai nói tên Phở gốc tiếng\r\nMỹ! <br />\r\n		\r\n<br />\r\n		\r\nChúng ta biết ông Nguyễn Tuân có viết một tuỳ bút về Phở đọc rất thú vị, ông Vũ\r\nBằng đã ca ngợi Phở hết lời. Vũ Bằng nhắc đến một gánh Phở rong ở Hà Nội tên là\r\nPhở Tráng, trước cửa Trường Tiểu học Nguyễn Công Trứ, gần phố Hàng Than trước\r\nđây hơn nửa thế kỷ. Nhưng cả hai ông Nguyễn Tuân và Vũ Bằng đều là nhà văn,\r\nkhông phải chuyên gia nghiên cứu lịch sử. Họ tán về Phở nào ngon, Phở nào đúng\r\nvị, thì rất hay, nhưng rất chủ quan. Ông Nguyễn Tuân coi chỉ có Phở chín mới\r\nđúng là Phở chính thống, ăn Phở tái là theo chủ nghĩa x&eacute;t lại nguy hiểm! Nhưng\r\nbây giờ có hàng phở nào thiếu món Phở tái hay không? Ông căn cứ vào đâu mà quả\r\nquyết như vậy!<br />\r\n		\r\n<br />\r\n		\r\nCần các sử gia về Phở! Chưa có ai nghiên cứu tường tận coi những bát Phở ông bà\r\nchúng ta ăn cách đây 100 năm giống và khác những bát Phở chúng ta ăn bây giờ\r\nnhư thế nào? Chưa ai theo dõi xem những bát Phở đi di cư từ Bắc vào Nam đã được\r\n"thay đổi sáng tạo" ra sao, so với những bát Phở Bắc Kỳ cũ đi vào Nam\r\nlàm đồn điền cao su. Nhất là cuộc di cư vĩ đại của những tô Phở từ cả hai miền\r\nNam và Bắc Việt Nam đi ra khắp thế giới, hai òng Phở khác nhau cùng xuất ngoại,\r\nthiên biến vạn hoá như thế nào. <br />\r\n		\r\n<br />\r\n		\r\nTôi đã ăn Phở ở Montr&eacute;al, Toronto, Canada, ở California bên Mỹ, ở Paris nước\r\nPháp, đã thấy khác nhau rồi. Nhưng mời quý vị nếm thử những bát Phở ở một khu\r\nChợ Vòm bên Warsaw (Ba Lan); trong Chợ Hổ tại Budapest (Hungary); hay ở\r\nMátxcơva bên Nga. Những bát Phở từ miền Bắc di cư sang Đông Âu và những tô Phở\r\ntừ miền Nam vượt biển sang các nước Âu, Mỹ, Úc có mùi vị khác nhau. Phở Hà Nội\r\nchắc chắn khác Phở Sài Gòn, Phở trong nước và Phở hải ngoại bây giờ cũng khác\r\nnhau. Phải nếm thử mới biết. <br />\r\n		\r\n<br />\r\n		\r\nMùa hè năm 2007 tôi sang Phần Lan. Tới Helsinki, nơi Nguyễn Tuân đã từng ngồi\r\nnhớ Phở nửa thế kỷ trước, tôi mới được "về với mẹ cha!" Rất tiếc tôi\r\nkhông có chút năng khiếu nào về chuyện ăn và uống, cho nên không dám động tới\r\nnhững đề tài cao quý đó, sợ mang tội vọng ngôn. Phải có những chuyên viên ẩm\r\nthực mới dám nghiền ngẫm, đắn đo, nếm náp, so sánh, suy nghĩ, và bàn luận về\r\nnhững chuyện vĩ đại như chuyện Phở.<br />\r\n		\r\n<br />\r\n		\r\n<span style="font-weight: bold; ">Cần sử gia thứ thiệt<br />\r\n			\r\n</span>Nhưng đáng tiếc chúng ta không có một sử gia nào chuyên nghiên cứu về Phở.\r\nThiếu những công trình nghiên cứu khoa học đứng đắn, mai mốt có khi con cháu\r\nchúng ta ở Mỹ không còn biết Phở là món ăn của người Việt mang tới nữa; có khi\r\nlại tưởng Phở là một món ăn Hàn Quốc! Vì dân Cao Ly ở Mỹ cũng đang mở tiệm Phở\r\nlấy trong các Korea Towns, họ không cần thuê đầu bếp Việt Nam nữa! <br />\r\n		\r\n<br />\r\n		\r\nMột điều đáng báo động là báo chí sách vở sẽ viết rất nhiều điều sai lạc về Phở\r\nmà không có một chuyên gia có thẩm quyền nào đứng ra đính chính. Thiếu một giáo\r\nsư sử học chuyên về Phở. Không có những cơ quan giống như hàn lâm viện chuyên\r\nvề Phở. Cho nên nhiều ông, bà chủ tiệm Phở cứ nói nhăng nói cuội rồi báo Mỹ,\r\nbáo Pháp cứ thế in lên. Mai mốt có sử gia nào, có nhà nghiên cứu nào quan tâm\r\ntìm hiểu, họ mà căn cứ vào các tài liệu trên giấy trắng mực đen đó để viết lịch\r\nsử Phở thì chúng ta coi như mất gia phả!<br />\r\n		\r\n<br />\r\n		\r\nMối lo lắng này có thật, vì đại đa số các người mở tiệm Phở ở nước ngoài đều là\r\ndân "nhảy dù" vào nghề nấu Phở. Có một anh mở tiệm Phở ở Montr&eacute;al,\r\nnơi tôi đã sống hơn 20 năm. Anh tuyên bố rằng gia đình anh đã nấu phở từ bốn\r\nđời! Các cụ đã dày công nghiên cứu cải thiện món "súp tôngkinoa"\r\n(Tonkinoise - Bắc Kỳ - BT) này cho tới chỗ tuyệt hảo, đã ghi thành bí kíp chỉ\r\ntruyền cho con trai thôi. Từ Hà Nội vào đến Sài Gòn rồi qua Montr&eacute;al, con cháu\r\nkhông bao giờ dám bỏ tổ nghiệp! Cứ nghe anh ta nói thì tưởng như chuyện Nhị\r\nThập Tứ Hiếu, nghe rớt nước mắt được! Nhưng bà con chung quanh ở Montr&eacute;al đều\r\nbiết cụ thân sinh ra anh vốn là một công chức, mẹ anh làm tiểu thương, anh đi\r\nhọc kỹ sư, vợ anh cũng là một cô giáo! Trong nhà chẳng có ông con trai nào biết\r\nnấu nướng bao giờ, cho đến khi phải chạy sang Canada tị nạn mới làm nên sự\r\nnghiệp Phở! Đúng là thời thế tạo anh hùng. <br />\r\n		\r\n<br />\r\n		\r\nNhưng thử tưởng tượng các sử gia sau này vào thư viện hay vào Google trên\r\nInternet nghiên cứu những tài liệu đó! Hiện nay còn bao nhiêu ông bà chủ tiệm\r\nPhở khắp thế giới đang "bốc ph&eacute;t" về nghề nghiệp của mình với mục\r\nđích marketing, nói cho "bọn Tây nó" ghi ch&eacute;p! Chúng ta sẽ có ngày\r\nhối hận vì không ai lập một quỹ học bổng dành riêng cho các sinh viên cao học\r\nvà tiến sĩ muốn nghiên cứu về Phở!<br />\r\n		\r\n<br />\r\n		\r\nBiết bao giờ mới có một sử gia viết về món Phở của nước ta với tấm lòng nhiệt thành\r\nvà kính ngưỡng? Bây giờ nếu có một sinh viên người Việt nào tình nguyện đi làm\r\nluận án tiến sĩ về lịch sử Phở, tôi chắc bố mẹ sẽ mời ra khỏi nhà ngay, không\r\nchịu trả tiền học cho con nữa! Nếu có một nhóm Mạnh Thường Quân chung nhau mỗi\r\nnăm một chục ngàn làm học bổng trong vòng ba bốn năm, chắc sẽ có một bạn trẻ\r\ndám đi khai phá một môn học mới, sau này sẽ gọi là Phở học. Khó tìm ra những\r\nMạnh Thường Quân như vậy!</p></p>', '2012-03-17 00:00:00', '2012-03-17 00:00:00', 1, 'Ai viết lịch sử phở', 0, 0, 'vi', ',6,56,', 'admin', '', 0);
INSERT INTO `web_info` VALUES (110, 'Văn Hóa Ẩm Thực Người Việt Nam', 'Van-Hoa-Am-Thuc-Nguoi-Viet-Nam-110', 'Van-Hoa-Am-Thuc-Nguoi-Viet-Nam.jpg', 'Văn hóa ẩm thực là nét văn hóa tự nhiên hình thành trong cuộc sống.', '\r\n\r\n\r\n<p style="text-align: justify;">\r\n	</p>\r\n\r\n<p>\r\n	</p>\r\n<p>\r\n		\r\n	<img src="/upload/tin-tuc//Van-Hoa-Am-Thuc-Nguoi-Viet-Nam.jpg" alt="" align="left" style="margin-right: 5px; " border="0" /><span style="font-style: italic; ">Văn hóa ẩm thực là n&eacute;t văn hóa tự\r\nnhiên hình thành trong cuộc sống. Nhất là đối với người Việt Nam, ẩm thực không\r\nchỉ là n&eacute;t văn hóa về vật chất mà còn là văn hóa về tinh thần. Qua ẩm thực\r\nngười ta có thể hiểu được n&eacute;t văn hóa thể hiện phẩm giá con người, trình độ văn\r\nhóa của dân tộc với những đạo lý, ph&eacute;p tắc, phong tục trong cách ăn uống...</span></p>\r\n\r\n\r\n	\r\n<p>&nbsp;</p>\r\n\r\n\r\n	\r\n<p>Đặc biệt đối với giới doanh nhân, việc\r\nnắm bắt được những n&eacute;t văn hóa ẩm thực của dân tộc là điều hết sức cần thiết.\r\nNó thể hiện phong cách của người chủ doanh nghiệp trước các đối tác, nhất là\r\nđối với các đối tác nước ngoài. </p>\r\n\r\n\r\n	\r\n<p><span style="font-weight: bold; ">N&eacute;t văn hóa ẩm thực người Việt</span></p>\r\n\r\n\r\n	\r\n<p>Văn hóa ẩm thực người Việt được biết\r\nđến với những n&eacute;t đặc trưng như: tính hòa đồng, đa dạng, ít mỡ; đậm đà hương vị\r\nvới sự kết hợp nhiều loại gia giảm để tăng mùi vị, sức hấp dẫn trong các món\r\năn. Việc ăn thành mâm và sử dụng đũa và đặc biệt trong bữa ăn không thể thiếu\r\ncơm là tập quán chung của cả dân tộc Việt Nam. </p>\r\n\r\n\r\n	\r\n<p>Bên cạnh những n&eacute;t chung đó thì mỗi\r\nmột vùng miền lại có những n&eacute;t đặc trưng ẩm thực riêng: </p>\r\n\r\n\r\n	\r\n<p><span style="font-weight: bold; "><span style="font-style: italic; ">Ẩm thực miền Bắc</span></span>: món ăn có vị vừa phải, không quá nồng nhưng lại có màu sắc\r\nsặc sỡ, thường không đậm các vị cay, b&eacute;o, ngọt, chủ yếu sử dụng nước mắm loãng,\r\nmắm tôm. Hà Nội được xem như tinh hoa ẩm thực của miền Bắc với những món ăn\r\nngon như phở, bún thang, bún chả, bún ốc, cốm làng Vòng, bánh cuốn Thanh trì và\r\ngia vị đặc sắc như tinh dầu cà cuống, rau húng Láng.</p>\r\n\r\n\r\n	\r\n<p><span style="font-weight: bold; "><span style="font-style: italic; ">Ẩm thực miền Trung</span></span>: Người miền Trung lại ưa dùng các món ăn có vị đậm hơn,\r\nnồng độ mạnh. Tính đặc sắc thể hiện qua hương vị đặc biệt, nhiều món cay hơn đồ\r\năn miền Bắc và miền nam. Màu sắc được phối trộn phong phú, rực rỡ, thiên về màu\r\nđỏ và nâu sậm. Ẩm thực miền Trung nổi tiếng với mắm tôm chua, các loại mắm\r\nruốc. Ẩm thực cung đình Huế với phong cách ẩm thực hoàng gia không chỉ rất cay,\r\nrất nhiều màu sắc mà còn chú trọng vào số lượng các món ăn, cách bày trí món. </p>\r\n\r\n\r\n	\r\n<p><span style="font-weight: bold; "><span style="font-style: italic; ">Ẩm thực miền Nam</span></span>: Do chịu nhiều ảnh hưởng của ẩm thực Trung Hoa, Campuchia,\r\nThái Lan nên các món ăn của người miền Nam thiên về độ ngọt, độ cay. Phổ biến\r\ncác loại mắm khô như mắm cá sặc, mắm bò hóc, mắm ba khía... Có những món ăn dân\r\ndã, đặc thù như: chuột đồng khìa nước dừa, dơi quạ hấp chao, rắn hổ đất nấu\r\ncháo đậu xanh, đuông dừa, đuông đất hoặc đuông chà là, vọp chong, cá lóc nướng\r\ntrui... </p>\r\n\r\n\r\n	\r\n<p><span style="font-weight: bold; "><span style="font-style: italic; ">Ẩm thực các dân tộc thiểu số</span></span>: Ẩm thực của mỗi dân tộc thiểu số đều có những bản sắc\r\nriêng biệt. Nổi tiếng như món thịt lợn sống trộn phèo non của các dân tộc Tây\r\nNguyên, bánh cuốn trứng (Cao Bằng, Lạng Sơn), bánh coong phù dân tộc Tày, Lợn\r\nsữa và vịt quay móc mật, khau nhục Lạng Sơn, phở chua, cháo nhộng ong, phở cồn\r\nsủi, thắng cố, các món xôi nếp nương của người Thái, thịt chua Thanh Sơn Phú\r\nThọ... </p>\r\n\r\n\r\n	\r\n<p><span style="font-weight: bold; ">Ẩm thực thể hiện văn hóa tinh thần\r\nngười Việt</span></p>\r\n\r\n\r\n	\r\n<p>Văn hóa tinh thần của người Việt\r\ntrong ẩm thực chính là sự thể hiện n&eacute;t đẹp trong văn hóa giao tiếp, là sự cư xử\r\ngiữa người với người trong bữa ăn, làm vui lòng nhau qua thái độ ứng xử lịch\r\nlãm, có giáo dục. Việc ăn uống đều có những ph&eacute;p tắc, lề lối riêng, từ bản\r\nthân, đến trong gia đình, rồi các mối quan hệ ngoài xã hội. </p>\r\n\r\n\r\n	\r\n<p align="center">\r\n		\r\n	<img src="http://dayamthuc.vn/upload/tin-tuc//Van-Hoa-Am-Thuc-Nguoi-Viet-Nam2.jpg" alt="" align="" border="0px" style="text-align: -webkit-auto; " />\r\n</p>\r\n\r\n\r\n	\r\n<p>Bản thân mỗi người phải biết giữ\r\ngìn, thận trọng trong khi ăn, cũng như đề cao danh dự của mình: “ăn trông nồi,\r\nngồi trông hướng”, hay “ăn phải nhai, nói phải nghĩ”</p>\r\n\r\n\r\n	\r\n<p>Trong gia đình: ăn chung mâm, ưu\r\ntiên thức ăn ngon cho người lớn tuổi, trẻ nhỏ"kính trên nhường dưới",\r\nthể hiện sự kính trọng, tình cảm yêu thương. Bữa cơm hàng ngày được xem là bữa\r\ncơm xum họp gia đình, mọi người quây quần bên nhau, cùng nhau vui vầy sau một ngày\r\nlàm việc mệt nhọc.</p>\r\n\r\n\r\n	\r\n<p>Ngoài xã hội: việc mời khách đến nhà\r\nthể hiện n&eacute;t văn hóa giữa người với người trong xã hội. Khi có dịp tổ chức ăn\r\nuống, gia chủ thường làm những món ăn thật ngon, nấu thật nhiều để đãi khách.\r\nChủ nhà thường gắp thức ăn mời khách, tránh việc dừng đũa trước khách, và có\r\nlời mời ăn thêm khi khách dừng bữa. Bữa cơm thiết không chỉ đơn thuần là cuộc\r\nvui mà còn thể hiện tấm lòng hiếu khách đặc trưng của người Việt.</p>\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', '2012-03-17 00:00:00', '2012-03-17 00:00:00', 1, 'Văn Hóa Ẩm Thực Người Việt Nam', 0, 0, 'vi', ',6,56,', 'admin', '', 0);
INSERT INTO `web_info` VALUES (112, 'Bếp chính nâng cao - Bếp trưởng', 'Bep-Chinh-Nang-Cao-Bep-Truong-112', 'bep-truong-nha-hang.jpg', 'Khai giảng: <br /><font color=red><b>14:00 - 17:00 ngày 16/04/2013</b></font>', '\r\n<p style="text-align: center;"><span style="text-align: start; font-weight: bold;">Khai giảng:&nbsp;</span><span style="font-weight: bold;"><span style="color: rgb(255, 20, 147);">14:00 - 17:00 ngày&nbsp;</span></span><span style="font-weight: bold; color: rgb(255, 20, 147);">16/04/2013</span></p>\r\n\r\n\r\n<p style="text-align: center;"><a href="http://www.dayamthuc.vn/dang-ky-truc-tuyen.html">\r\n		\r\n		\r\n		<img src="http://www.dayamthuc.vn/upload/tin-tuc/dang-ky-truc-tuyen.gif" alt="đăng ký trực tuyến" align="" border="0px" /></a></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">1. Đặc điểm chương trình: </span>Để trở thành Bếp Trưởng Nhà Hàng, Bạn phải qua các lớp huấn luyện về bếp và có kinh nghiệm làm việc với các vị trí Phụ bếp, Bếp chính (Chảo). </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Bếp trưởng là Người chỉ huy toàn bộ các nhân sự và hoạt động bếp, do đó đòi hỏi Bạn phải được đào tạo các kỹ năng về tổ chức bếp, kỹ năng lãnh đạo đội ngũ bếp, ngoài ra Bạn là Người đưa ra các thực đơn, món ăn độc đáo, giúp Nhà hàng thành công.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Trường NetSpace đã xây dựng Chương trình đào tạo Bếp Trưởng Nhà hàng dành cho Các bạn đã qua các khóa huấn luyện về Phụ bếp, Bếp chính hoặc có kinh nghiệm làm việc trên 1 năm trong nghề bếp .</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Tham gia Chương trình này, Học viên được học với các Bếp Trưởng, các Chuyên Gia Ẩm Thực hàng đầu Việt Nam, được thực tập và có cơ hội thăng tiến hoặc cơ hội việc làm tại các Nhà hàng, Khách sạn, Khu Du Lịch lớn.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(255, 0, 0); "><span style="font-weight: bold; ">2. Thời gian học</span>:</span> 3 tháng</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; "><span style="color: rgb(255, 0, 0); ">3. Chứng chỉ hoàn tất khóa học:</span></span>do Cơ Sở Dạy Nghề Ẩm Thực NetSpace cấp có giá trị toàn quốc và Hội đầu bếp Chuyên nghiệp Sài Gòn chứng nhận.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">4. Học phí:</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<div>10.000.000 đ/khóa (Đã bao gồm chi phí nguyên vật liệu thực hành và đồng phục bếp)</div>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<div><span style="font-style: italic;">(Có thể đóng 2 lần, mỗi lần 5.000.000đ)<br />\r\n		\r\n		\r\n		\r\n		\r\n		\r\n		\r\n		\r\n		\r\n		\r\n		<br />\r\n		\r\n		\r\n		\r\n		\r\n		\r\n		\r\n		\r\n		\r\n		\r\n		</span></div>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">5. Nội dung khóa học</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">Phần 1: Cách chế biến các món ăn cao cấp - Món tiệc (Bếp nâng cao) (19)</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>I. Các món ăn sáng</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<ol style="">\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<li>Mì vịt tiềm</li>\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<li>Bún Bò Huế</li>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</ol>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>II. Các món cơm</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<ol style="">\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<li>Cơm gà Hải Nam</li>\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<li>Cơm nướng hải sản</li>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n</ol>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>III. Các món khai vị</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<ol style="">\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<li>Nem Công</li>\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<li>Chả Phụng</li>\r\n	\r\n	\r\n	\r\n	\