INSERT INTO `web_info` VALUES (224, 'Học làm bánh và kinh nghiệm học nghề', 'hoc-lam-banh-va-kinh-nghiem-hoc-nghe-224', 'ban-se-thanh-cong-trong-nghe-banh-neu-ban-co-tinh-than-cau-tien.jpg', 'Học làm bánh và nghề bánh là nghề không xa lạ gì với chúng ta trong những năm gần đây. Không ít các bạn đã tham gia các khóa đào tạo học làm bánh ở nhiều nơi, nhưng không phải ai cũng làm được và thành công,  bởi vì chúng ta chưa tâm huyết với nghề làm bánh này.', '<h2>Học làm bánh và nghề bánh là nghề không xa lạ gì với chúng ta trong những năm gần đây. Không ít các bạn đã tham gia các khóa đào tạo học làm bánh ở nhiều nơi, nhưng không phải ai cũng làm được và thành công, &nbsp;bởi vì chúng ta chưa tâm huyết với nghề làm bánh này.</h2>\r\n\r\n<p style="text-align: center;"><img alt="Học làm bánh cần tâm huyết với nghề" src="/upload/images/articles/hoc-lam-banh-can-tam-huyet-voi-nghe.jpg" style="width: 550px; height: 385px;" /></p>\r\n\r\n<p style="text-align: center;"><em>Học làm bánh cần tâm huyết với nghề</em></p>\r\n\r\n<p><strong>Học làm bánh</strong>, bạn chỉ thích là chưa đủ, mà đòi hỏi bạn phải nhẫn nại, kiên trì và có tính sáng tạo cao, phần lớn các loại bánh đòi hỏi kỹ thuật điêu luyện, trang trí đẹp mắt, mang tính nghệ thuật cao nên chỉ một sơ suất nhỏ không tập trung, có thể làm hỏng hết cả một quá trình &nbsp;làm bánh của Bạn.</p>\r\n\r\n<p style="text-align: center;"><img alt="Học làm bánh cần phải có tính sáng tạo" src="/upload/images/articles/hoc-lam-banh-can-phai-co-tinh-sang-tao.jpg" style="width: 550px; height: 385px;" /></p>\r\n\r\n<p style="text-align: center;"><em>Học làm bánh cần phải có tính sáng tạo</em></p>\r\n\r\n<p><strong>Học làm bánh luôn cần một đôi tay khéo léo</strong>, mềm dẻo song cũng đầy sức sáng tạo và các bạn cần đầu tư nhiều thời gian vào việc học hỏi, tìm hiểu các khâu làm bánh. Các bạn luôn nghĩ rằng khi các bạn thực hành học làm bánh cũng chính là các bạn đang làm bánh cho khách hàng.</p>\r\n\r\n<p style="text-align: center;"><img alt="Học làm bánh cần có sự sáng tạo" src="/upload/images/articles/hoc-lam-banh-can-co-su-sang-tao.jpg" style="width: 550px; height: 385px;" /></p>\r\n\r\n<p style="text-align: center;"><em>Học làm bánh cần có sự sáng tạo</em></p>\r\n\r\n<p>Dù <strong>học làm bánh</strong> hay học bất cứ ngành nghề nào thì bạn phải tinh thần học hỏi và thực hành nhiều lần.</p>\r\n\r\n<p style="text-align: center;"><img alt="Bạn sẽ thành công trong nghề bánh nếu bạn có tinh thần cầu tiến" src="/upload/images/articles/ban-se-thanh-cong-trong-nghe-banh-neu-ban-co-tinh-than-cau-tien.jpg" style="width: 550px; height: 385px;" /></p>\r\n\r\n<p style="text-align: center;"><em><span style="line-height: 1.6em;">Bạn sẽ thành công trong nghề bánh nếu bạn có tinh thần cầu tiến</span></em></p>\r\n\r\n<p>Tinh thần cầu tiến, năng lực bản thân, kiên nhẫn trong mọi tình huống, sáng tạo mọi lúc mọi nơi sẽ là những phần quan trọng góp phần trong việc<strong> học làm bánh</strong> của Bạn. Cơ hội thành công chỉ dành cho những người tâm huyết với nghề và <a href="http://www.dayamthuc.vn"><strong>Trường Dạy Nghề Ẩm Thực Netspace</strong></a> sẽ sát cánh đồng hành con đường phát triển nghề nghiệp của các bạn.</p>\r\n\r\n<p><a href="http://www.dayamthuc.vn/xem-tin/Khoa-Hoc-Lam-Banh-Au-217.html"><span style="color:#B22222;"><span style="font-size:16px;"><strong>Xem chi tiết khóa học làm&nbsp;bánh Âu vui lòng ấn vào đây.</strong></span></span></a></p>\r\n\r\n<p style="text-align: right;"><strong>Hồng Hảo ghi</strong></p>\r\n', '2013-05-31 10:25:56', '2013-05-31 10:25:56', 1, 'Học làm bánh và kinh nghiệm học nghề,Học làm bánh', 0, 0, 'vi', ',6,56,', 'admin', NULL, 0);
INSERT INTO `web_info` VALUES (132, '15 món ngon Việt được đề cử kỷ lục châu Á', '15-Mon-Ngon-Viet-Duoc-De-Cu-Ky-Luc-Chau-A-132', '15-mon-ngon-viet-duoc-de-cu-ky-luc-chau-a.jpg', 'Phở, chả cá, bún chả Hà Nội, bánh đa cua Hải Phòng, cơm cháy Ninh Bình, gỏi cuốn Sài Gòn', '\r\n<p style="text-align: justify;">Phở, chả cá, bún chả Hà Nội, bánh đa cua Hải Phòng, cơm cháy Ninh Bình, gỏi cuốn Sài Gòn... vượt qua hàng trăm món ăn khác của Việt Nam để được chọn vào danh sách đề cử 15 món ngon nhận kỷ lục châu Á.</p>\r\n\r\n\r\n<p style="text-align: justify;">Danh sách đề cử 15 món ngon Việt gồm có: Phở - bún chả - chả cá Lã Vọng - bún thang của Hà Nội, bánh đa cua Hải Phòng, cơm cháy Ninh Bình, miến lươn Nghệ An, bún bò Huế, mì Quảng, phở khô Gia Lai, bánh canh Trảng Bàng, bánh khọt Vũng Tàu, gỏi cuốn và cơm tấm Sài Gòn, bánh cóng Sóc Trăng. Đây được xem là những món ăn chỉ ở Việt Nam mới có khi so sánh với các nước khu vực châu Á.</p>\r\n\r\n\r\n<table cellspacing="0" cellpadding="3" width="1" align="center" border="0" style="text-align: justify;">\r\n	\r\n	\r\n	<tbody>\r\n		\r\n		\r\n		<tr>\r\n			\r\n			\r\n			<td>\r\n				\r\n				\r\n				<p align="center">\r\n					\r\n					\r\n					<img src="/upload/tin-tuc//15-mon-ngon-viet-duoc-de-cu-ky-luc-chau-a.jpg" width="495" height="393" alt="Phở hấp dẫn người ăn vì nước dùng (nước lèo) có hương vị rất ngon và bổ dưỡng do được phối hợp từ nhiều loại gia vị và nguyên liệu khác nhau kết hợp với bánh phở vừa mềm vừa dai ăn rất hấp dẫn. Thành phần chính của phở là bánh phở và nước dùng cùng với thịt bò hay gà cắt lát mỏng. Ngoài ra còn kèm theo các gia vị như: tương, tiêu, chanh, nước mắm, ớt... Những gia vị này được thêm vào tùy theo khẩu vị của từng người dùng." align="" style="" border="0" /></p></td>\r\n		\r\n		\r\n		</tr>\r\n		\r\n		\r\n		<tr>\r\n			\r\n			\r\n			<td style="text-align: left;">Phở hấp dẫn người ăn vì nước dùng (nước lèo) có hương vị rất ngon và bổ dưỡng do được phối hợp từ nhiều loại gia vị, nguyên liệu khác nhau, kết hợp với bánh phở vừa mềm vừa dai ăn rất hấp dẫn.</td>\r\n		\r\n		\r\n		</tr>\r\n	\r\n	\r\n	</tbody>\r\n\r\n\r\n</table>\r\n\r\n\r\n<p style="text-align: justify;">Danh sách đợt một này sẽ được gửi đến Tổ chức Kỷ lục châu Á x&eacute;t duyệt vào tháng 9 tới.</p>\r\n\r\n\r\n<p style="text-align: justify;">Đại diện Tổ chức kỷ lục Việt Nam Vietkings cho biết, các món trên được chọn ra sau khi tổ chức này khảo sát hàng trăm món ăn trên toàn quốc cũng như khảo sát bước đầu về các món ăn từ 57 quốc gia và vùng lãnh thổ của châu Á.</p>\r\n\r\n\r\n<table cellspacing="0" cellpadding="3" width="1" align="center" border="0" style="text-align: justify;">\r\n	\r\n	\r\n	<tbody>\r\n		\r\n		\r\n		<tr>\r\n			\r\n			\r\n			<td>\r\n				\r\n				\r\n				<p align="center">\r\n					\r\n					\r\n					<img src="/upload/tin-tuc//15-mon-ngon-viet-duoc-de-cu-ky-luc-chau-a-1.jpg" width="495" height="359" alt="Hầu như mỗi góc phố, góc chợ của Sài Gòn - thành phố Hồ Chí Minh đều có một hàng gỏi cuốn. Món gỏi cuốn tưởng chừng đơn giản nhưng làm ngất ngây nhiều người, đặc biệt du khách đến từ các tỉnh thành trong nước và cả du khách quốc tế." align="" style="" border="0" /></p></td>\r\n		\r\n		\r\n		</tr>\r\n		\r\n		\r\n		<tr>\r\n			\r\n			\r\n			<td style="text-align: left;">Hầu như mỗi góc phố, góc chợ của Sài Gòn đều có một hàng gỏi cuốn. Món gỏi cuốn tưởng chừng đơn giản nhưng làm ngất ngây nhiều người, đặc biệt du khách đến từ các tỉnh thành trong nước và cả du khách quốc tế.</td>\r\n		\r\n		\r\n		</tr>\r\n	\r\n	\r\n	</tbody>\r\n\r\n\r\n</table>\r\n\r\n\r\n<p style="text-align: justify;">Trong 57 quốc gia và vùng lãnh thổ của châu Á, Việt Nam được xem là một trong những nước có văn hóa ẩm thực độc đáo và đa dạng. Sự độc đáo ấy có được từ những món ăn truyền thống kết hợp với những bí quyết và công nghệ chế biến chúng, đến các sản vật và chế phẩm đi kèm, và kể cả triết lý “âm dương” trong việc kết hợp các nguyên liệu, gia vị của món ăn sao cho hài hòa, đảm bảo dinh dưỡng và tốt cho sức khỏe.</p>\r\n\r\n\r\n<table cellspacing="0" cellpadding="3" width="1" align="center" border="0" style="text-align: justify;">\r\n	\r\n	\r\n	<tbody>\r\n		\r\n		\r\n		<tr>\r\n			\r\n			\r\n			<td>\r\n				\r\n				\r\n				<p align="center">\r\n					\r\n					\r\n					<img src="/upload/tin-tuc//15-mon-ngon-viet-duoc-de-cu-ky-luc-chau-a-2.jpg" width="495" height="371" align="" alt="" style="" border="0" /></p></td>\r\n		\r\n		\r\n		</tr>\r\n		\r\n		\r\n		<tr>\r\n			\r\n			\r\n			<td style="text-align: left;">Chả cá Lã Vọng Hà Nội.</td>\r\n		\r\n		\r\n		</tr>\r\n	\r\n	\r\n	</tbody>\r\n\r\n\r\n</table>\r\n\r\n\r\n<p style="text-align: justify;">Philip Kotler, cha đẻ của marketing hiện đại, đã gợi ý về việc tại sao Việt Nam lại không phấn đấu để có được thương hiệu là "bếp ăn" của thế giới; trong khi Trung Quốc đã trở thành "công xưởng" của thế giới, Ấn Độ thành "văn phòng" của thế giới.</p>\r\n\r\n\r\n<p style="text-align: justify;">Ẩm thực được xem là thế mạnh, một trong những yếu tố quan trọng thu hút khách du lịch đến với Việt Nam. Chính vì thế, từ đầu năm nay, Vietkings triển khai Hành trình quảng bá ẩm thực và đặc sản Việt, thông qua việc tìm kiếm kỷ lục ẩm thực và đề cử đến Tổ chức kỷ lục Asean, Tổ chức kỷ lục châu Á và Tổ chức Kỷ lục thế giới Guinness, góp phần giúp ẩm thực Việt Nam được thế giới biết đến nhiều hơn nữa.</p>\r\n\r\n\r\n<p style="text-align: justify;">Tổ chức Kỷ lục Việt Nam sẽ tiếp tục khảo sát và đề cử nhiều món ăn nổi tiếng, độc đáo của Việt Nam để gửi đến Tổ chức kỷ lục châu Á trong thời gian tới.</p>\r\n\r\n\r\n<p align="right">\r\n	</p>\r\n<div style="text-align: right;"><span style="font-weight: bold; ">Thất Sơn</span></div><span style="font-style: italic; ">\r\n		\r\n	<div style="text-align: right;">Ảnh: Vietkings</div>\r\n		\r\n	<div style="text-align: right;">Theo vnexpress</div></span>\r\n<p>&nbsp;</p>', '2012-06-25 00:00:00', '2012-06-25 00:00:00', 1, '15 món ngon Việt được đề cử kỷ lục châu Á', 0, 0, 'vi', ',6,56,', 'admin', '', 0);
INSERT INTO `web_info` VALUES (133, 'Nơi hội tụ niềm đam mê ẩm thực Việt', 'Noi-Hoi-Tu-Niem-Dam-Me-Am-Thuc-Viet-133', 'Noi-Hoi-Tu-Niem-Dam-Me-Am-Thuc-Viet-2.jpg', 'Câu lạc bộ (CLB) Ẩm thực Việt thuộc Hội đầu bếp chuyên nghiệp Sài Gòn là nơi quy tụ nhiều tài năng ẩm thực', '\r\n<p><span style="font-weight: bold; "><span style="font-style: italic; ">Câu lạc bộ (CLB) Ẩm thực\r\nViệt thuộc Hội đầu bếp chuyên nghiệp Sài Gòn là nơi quy tụ nhiều tài năng ẩm\r\nthực Việt, họ cùng chung niềm đam mê, sở thích và mong muốn phát huy tiềm năng\r\nẩm thực Việt để quảng bá cho tất cả mọi người.</span></span></p>\r\n\r\n\r\n\r\n\r\n<p>Gặp và trò chuyện với TS\r\nNguyễn Thị Diệu Thảo - Chủ nhiệm Câu lạc bộ Ẩm thực Việt, chúng tôi phần nào\r\nhiểu hơn về vai trò, hoạt động của CLB cũng như niềm đam mê ẩm thực của những\r\nngười ở đây. CLB được thành lập xuất phát từ yêu cầu có nơi để sinh hoạt, đóng\r\ngóp của những người yêu ẩm thực Việt. Hơn nữa, Hội đầu bếp chuyên nghiệp Sài\r\nGòn hiện nay hơn 700 hội viên, với số lượng hội viên lớn mạnh theo từng ngày\r\nnên Hội cần chia thành các CLB để dễ dàng quản lý, cũng như trao đổi kinh\r\nnghiệm chuyên sâu. Tại Hội đầu bếp chuyên nghiệp Sài Gòn bên cạnh số lượng đầu\r\nbếp lớn còn có những chuyên gia nghiên cứu và giảng dạy ẩm thực Việt. Có thể\r\nnói, đây là một thế mạnh của CLB bếp Việt so với các CLB khác như bếp Hoa, bếp\r\nTrẻ…, thế mạnh ấy sẽ tạo cho CLB có cơ sở nền tảng để phát triển về sau.</p>\r\n\r\n\r\n\r\n\r\n<p align="center">\r\n \r\n \r\n  \r\n  \r\n  \r\n  \r\n  \r\n  \r\n  \r\n  \r\n  \r\n  \r\n  \r\n  \r\n \r\n \r\n \r\n\r\n \r\n\r\n	\r\n	\r\n	<img width="450" height="345" src="/upload/tin-tuc//Noi-Hoi-Tu-Niem-Dam-Me-Am-Thuc-Viet.jpg" alt="Noi-Hoi-Tu-Niem-Dam-Me-Am-Thuc-Viet" v:shapes="Picture_x0020_1" align="" style="" border="0" />&nbsp;</p>\r\n\r\n\r\n\r\n\r\n<p>Được thành lập vào tháng\r\n11/2011, tuy tuổi đời còn khá trẻ, nhưng khi mới ra đời CLB đã chứng minh sức\r\nmạnh và tiềm năng của mình bằng các hoạt động thiết thực như lập kỷ lục 100 đầu\r\nbếp tham gia món gỏi, tổ chức huấn luyện cho đầu bếp, tổ chức kỹ năng về bếp\r\ncho hội viên, kết hợp với siêu thị mela biểu diễn món ăn cho du khách, tham gia\r\nhội thảo đào tạo và sử dụng nguồn nhân lực nghề bếp trong du lịch… Những người\r\ntrong CLB hoặc trong Hội thường xuyên xung kích trong công tác từ thiện tại các\r\ntrại trẻ mồ côi, trung tâm nuôi dưỡng người già… Đặc biệt, họ đem theo những\r\nthức ăn đến các trung tâm nấu những bữa ăn ngon cho các trẻ em khuyết tật, trẻ\r\nem nghèo…, những hành động nhỏ ấy lại mang ý nghĩa cộng đồng lớn lao để một số\r\nngười khó khăn có thể có những bữa ăn ngon, ấm lòng.</p>\r\n\r\n\r\n\r\n\r\n<p align="center">\r\n \r\n\r\n	\r\n	\r\n	<img width="466" height="313" src="/upload/tin-tuc//Noi-Hoi-Tu-Niem-Dam-Me-Am-Thuc-Viet-2.jpg" alt="Noi-Hoi-Tu-Niem-Dam-Me-Am-Thuc-Viet-2" v:shapes="Picture_x0020_2" align="" style="" border="0" />&nbsp;</p>\r\n\r\n\r\n\r\n\r\n<p align="center"><span style="font-style: italic; ">Các thành viên của Hội đầu bếp chuyên nghiệp Sài Gòn và Câu lạc bộ\r\nẨm thực Việt trong ngày xác lập kỷ lục nhiều món gỏi nhất Việt Nam</span></p>\r\n\r\n\r\n\r\n\r\n<p>Hiện tại, CLB đang phối\r\nhợp với trường để đào tạo Netspace để đào tạo, nâng cao tay nghề cho những hội\r\nviên trong CLB nhằm hướng đến tính chuyên nghiệp hóa trong công việc. Hơn nữa,\r\nđây còn là nơi đào tạo bếp cho các bà nội trợ để họ phục vụ gia đình và mong\r\nmuốn có kiến thức trong việc làm bếp. </p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; ">Nghề bếp dễ nhưng khó</span></p>\r\n\r\n\r\n\r\n\r\n<p>Ở nước ta làm bếp từ xưa\r\nđến nay là sự truyền dạy giữa người này so với người khác, thế hệ này thế hệ\r\nkhác, một số người có khiếu nấu ăn chỉ cần lên mạng tra cứu công thức và làm\r\ntheo. Nhưng, những vấn đề này chỉ dừng lại ở mức độ phục vụ nhu cầu của từng cá\r\nnhân, gia đình mà chưa hướng đến tính đại chúng. Những người muốn hành nghề\r\nbếp, phục vụ nhu cầu ăn uống của mọi người không được vô trách nhiệm trong bất\r\ncứ việc gì. TS Nguyễn Thị Diệu Thảo cho biết: “Một người làm bếp chuyên nghiệp\r\nvà bài bản đôi khi phải quên đi khẩu vị của riêng mình để có những món ăn chất\r\nlượng. Những người học theo kiểu truyền nghề cũng chỉ dừng lại ở đầu bếp tầm\r\ntrung, không thể trở thành đầu bếp đẳng cấp và chuyên nghiệp được”. Do đó, nghề\r\nbếp rất dễ với tất cả mọi người, nhưng để trở thành một đầu bếp đẳng cấp thì\r\nrất khó, nó cần một quy trình đào tạo và chuẩn hóa bài bản theo từng nấc bậc cụ\r\nthể.</p>\r\n\r\n\r\n\r\n\r\n<p align="center">\r\n \r\n\r\n	\r\n	\r\n	<img width="425" height="319" src="/upload/tin-tuc//Noi-Hoi-Tu-Niem-Dam-Me-Am-Thuc-Viet-3.jpg" alt="Noi-Hoi-Tu-Niem-Dam-Me-Am-Thuc-Viet-3" v:shapes="Picture_x0020_3" align="" style="" border="0" /></p>\r\n\r\n\r\n\r\n\r\n<p align="center"><span style="font-style: italic; ">Ông Lý Sanh (ngoài cùng bên trái), cùng TS Nguyễn Thị Diệu Thảo\r\ntrong Hội thảo khoa học Nhu cầu sử dụng và đào tạo nguồn nhân lực Bếp Việt tại\r\nTP HCM diễn ra tại Đại học Sài Gòn</span></p>\r\n\r\n\r\n\r\n\r\n<p>Nhận và chủ nhiệm CLB TS\r\nNguyễn Thị Diệu Thảo mong muốn đem tình yêu ẩm thực của mình để phả vào lòng\r\nmọi người, và mong muốn là nơi sinh hoạt chính quy, trao đổi và truyền những bí\r\nquyết làm nghề lẫn nhau. Trong định hướng của CLB luôn mong muốn hướng đến mảng\r\nnghiên cứu và đào tạo với sự trợ giúp của trường Netspace. TS Diệu Thảo cũng\r\nmong muốn sắp tới CLB sẽ đẩy mạnh mảng nghiên cứu nhiều hơn để có những công\r\ntrình nghiên cứu chất lượng tương xứng với công tác đào tạo. </p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; ">Cần một nhà kinh doanh ẩm thực</span></p>\r\n\r\n\r\n\r\n\r\n<p>ThS Nguyễn Quốc Y - Chủ\r\ntịch Hội đồng quản trị Công ty CP Giáo dục Netspace cho biết: “Ở các nước lớn\r\nnhư Hàn Quốc, Nhật Bản luôn chú trọng đào tạo nghề, còn ở Việt Nam chưa được\r\nđầu tư đúng mức”. Từ những chương trình thiết thực, kinh nghiệm thực tế của\r\nnhững người giảng dạy cho học viên có cái nhìn thực tế. Ông đến với ẩm thực\r\nbằng một sự tình cờ. Sự tình cờ ấy đưa ông đến gần hơn với ẩm thực và mong muốn\r\nđóng góp cho ẩm thực Việt bằng các chương trình đào tạo thật thiết thực để kinh\r\ndoanh ẩm thực Việt.</p>\r\n\r\n\r\n\r\n\r\n<p align="center">\r\n \r\n\r\n	\r\n	\r\n	<img width="431" height="323" src="/upload/tin-tuc//Noi-Hoi-Tu-Niem-Dam-Me-Am-Thuc-Viet-4.jpg" alt="Noi-Hoi-Tu-Niem-Dam-Me-Am-Thuc-Viet-4" v:shapes="Picture_x0020_4" align="" style="" border="0" /></p>\r\n\r\n\r\n\r\n\r\n<p align="center"><span style="font-style: italic; ">Biểu diễn món ăn cho du khách tại siêu thị Mela</span></p>\r\n\r\n\r\n\r\n\r\n<p>Muốn kinh doanh ẩm thực\r\nhiệu quả trước tiên không chỉ có đầu bếp giỏi mà người kinh doanh cần có kinh\r\nnghiệm trong công tác quản lý, theo ông nấu ngon chưa đủ để kinh doanh mà còn\r\nphải PR cho nhà hàng, địa điểm kinh doanh, quản lý… Tất cả các khâu và công đoạn\r\nphải được thực hiện theo một quy trình thật chuyên nghiệp. Ông mong muốn khôi\r\nphục bữa ăn truyền thống gia đình để gắn kết các thành viên trong gia đình. </p>\r\n\r\n\r\n\r\n\r\n<p>Trong xu thế phát triển\r\nhiện nay, các cường quốc ẩm thực trước đây không còn sức hút đối với thực khách\r\ntrên toàn thế giới, ẩm thực Việt đang dần khẳng định được vai trò và vị trí mới\r\ncủa mình. Món ăn Việt không chỉ ngon, lành, độc đáo mà còn thể hiện được tài\r\nnăng và trí tuệ, sự chịu khó sáng tạo của người Việt. Với chương trình đào tạo\r\nnày, ông và TS Nguyễn Thị Diệu Thảo mong muốn đưa đầu bếp Việt làm việc trong\r\ncác nhà hàng cao cấp ở Việt Nam và sang hoạt động và quảng bá ẩm thực Việt ở\r\ncác nước. Bởi lẽ, hiện nay lực lượng đầu bếp được trẻ hóa và giỏi nên định kiến\r\nvề xã hội với những người làm nghề bếp được cải thiện.</p>\r\n\r\n\r\n\r\n\r\n<p>Buổi trò chuyện càng làm\r\nchúng tôi tin tưởng rằng, với tâm huyết của TS Nguyễn Thị Diệu Thảo và những\r\nngười yêu ẩm thực trong CLB, con đường ẩm thực Việt chuyên nghiệp từng ngày và\r\nbước ra thế giới đã và đang rút ngắn dần.</p>\r\n\r\n<span style="font-weight: bold; ">\r\n	\r\n	\r\n	<div style="text-align: right;">Cô Sơn</div></span>', '2012-07-02 00:00:00', '2012-07-02 00:00:00', 1, 'Nơi hội tụ niềm đam mê ẩm thực Việt', 0, 0, 'vi', ',6,56,', 'admin', '', 0);
INSERT INTO `web_info` VALUES (134, 'Cua trinh nữ hấp là sen', 'Cua-Trinh-Nu-Hap-La-Sen-134', 'Cua-Trinh-Nu-Hap-La-Sen.jpg', 'Với suy nghĩ người đầu bếp không chỉ biết làm ra những món ăn ngon mà còn phải biết kết hợp được các loại dinh dưỡng', '\r\n<p>Với suy nghĩ người đầu bếp không chỉ biết làm ra những món ăn ngon mà còn phải biết kết hợp được các loại dinh dưỡng và vị thuốc vào món ăn đó để mang lại cho thực khách những món ăn ngon và bổ dưỡng . Tôi đã tìm được cách kết hợp các loại thảo dược quý với cua long đỏ để có được món cua hấp lá sen vừa ngon lại bổ dưỡng.</p>\r\n<p>Món ăn này không chỉ cho thực khách cảm nhận được vị ngon ngọt của cua long đỏ,mà còn là món ăn để bồi bổ cơ thể cho mọi lứa tuổi.</p>\r\n<p style="text-align: center;">\r\n	<img src="/upload/tin-tuc//Cua-Trinh-Nu-Hap-La-Sen.jpg" alt="Cua-Trinh-Nu-Hap-La-Sen" align="" border="0px" /></p>\r\n<p><span lang="VI">Lá sen (còn gọi là </span>H<span lang="VI">à </span>D<span lang="VI">iệp), từ lâu đã được y học cổ truyền dùng làm thuốc chữa bệnh. Lá sen có công dụng an thần, chống co thắt cơ trơn, chống choáng phản vệ, ức chế loạn nhịp tim. Tác dụng an thần của lá sen mạnh hơn tâm (tim) sen, có tác dụng k&eacute;o dài giấc ngủ. Về sau này, khi bệnh b&eacute;o phì trở nên phổ biến, thì lá sen được sử dụng rất hiệu quả để chống lại căn bệnh này. Lá sen phối hợp với các vị thuốc sơn tr</span>à<span lang="VI">, hà thủ ô và thảo quyết minh (hạt muồng) pha trà uống thường xuyên sẽ có tác dụng giảm cholesterol cùng các tác nhân gây b&eacute;o phì.</span></p>\r\n<p style="text-align: center;"><span lang="VI">\r\n		<img src="/upload/tin-tuc//Cua-Trinh-Nu-Hap-La-Sen-2.jpg" alt="Cua-Trinh-Nu-Hap-La-Sen-2" align="" border="0px" /></span></p>\r\n<p align="center" style="text-align: left;"><span lang="VI">Hạt sen có chứa các chất dinh dưỡng như: tinh bột, chất đường, protein, chất b&eacute;o, canxi, phốt pho, sắt... là thức ăn bổ dưỡng tâm, ích thận, kiện tỳ, những người lao động trí óc quá căng thẳng, tâm thần bất ổn, hay sợ sệt, hay quên nên ăn</span>.</p>\r\n<p align="center"><span lang="VI"></span></p>\r\n<div style="text-align: -webkit-center;">\r\n	<img src="/upload/tin-tuc//Cua-Trinh-Nu-Hap-La-Sen-3.jpg" alt="Cua-Trinh-Nu-Hap-La-Sen-3" align="" border="0px" /></div>\r\n<p align="center" style="text-align: left;"><span lang="VI">Táo tàu còn có tác dụng bổ gan, tăng cường cơ bắp, hạ huyết áp, an thần, dễ ngủ, tránh hưng phấn mẫn cảm, hạn chế sự phát triển tế bào ung thư, làm tan đờm, giảm ho, cải thiện dinh dưỡng cơ tim...</span></p>\r\n<p><span lang="VI"></span></p>\r\n<div style="text-align: -webkit-center;">\r\n	<img src="/upload/tin-tuc//Cua-Trinh-Nu-Hap-La-Sen-4.jpg" alt="Cua-Trinh-Nu-Hap-La-Sen-4" align="" border="0px" /></div>\r\n<p align="center" style="text-align: left;"><span lang="VI">Theo Đông y, câu kỷ tử vị ngọt, tính bình, quy vào 3 kinh phế</span>:<span lang="VI"> can, thận, tác dụng nhuận phế, thanh can, tươi thận, thêm khí sinh tinh, giúp bổ hư lao, làm mạnh gân cốt, đặc biệt bổ thận tráng dương, tiêu trừ các chứng phong tí, hàn tí, thấp tí, làm thông lợi đại tràng, tiểu tràng, trừ phong, sáng mắt. Dùng chữa các bệnh yếu gan, yếu thận (can thận âm hư), lưng đau, gối mỏi, hoa mắt, chóng mặt, ho lao, tiêu khát, di tinh, chức năng sinh lý k&eacute;m.</span></p>\r\n<p><span lang="VI">Cua long đỏ là những con cua cái chưa </span>đ<span lang="VI">ến tuổi động dục nên thịt của nó có vị ngọt thanh rất đặc biệt,và từ lâu người ta tin là dinh dưỡng cao hơn những loại cua khác. Cũng vì được tuyển chọn từ những con cua cái chưa đến tuổi đông dục n</span>ê<span lang="VI">n tôi đã gọi đó là “cua trinh nữ”.</span></p>\r\n<p align="center"><span lang="VI"></span></p>\r\n<p align="center"><span lang="VI"></span></p>\r\n<div style="text-align: -webkit-center;">\r\n	<img src="/upload/tin-tuc//Cua-Trinh-Nu-Hap-La-Sen-5.jpg" alt="Cua-Trinh-Nu-Hap-La-Sen-5" align="" border="0px" /></div>\r\n<p><span lang="VI">Thành phần dinh dưỡng trong cua biển rất phong phú, hàm lượng protein cao hơn nhiều so với thịt heo hay cá. Ngoài ra canxi, photpho, sắt và các vitamin A, B1, B2, C... cũng chiếm ở mức cao. </span></p>\r\n<p><span lang="VI"></span></p>\r\n<p><span lang="VI">Cua biển còn chứa một lượng lớn calcium, magnesium và axít b&eacute;o omega 3, rất tốt cho tim mạch. Một số nghiên cứu dinh dưỡng còn ghi nhận cua giúp làm giảm cholesterol xấu và triglycerides trong máu. Thịt cua có vị ngọt, mặn, không độc, có tác dụng thanh nhiệt, sinh huyết, tán ứ, giảm đau, bổ xương tuỷ nên rất tốt cho cơ thể đang phát triển ở trẻ nhỏ và tình trạng suy yếu ở người cao tuổi.</span></p>\r\n<div style="text-align: right;"><span style="font-weight: bold; font-style: italic; ">DIỆP CHẤN HƯNG</span></div><span style="font-weight: bold; ">\r\n	<div style="text-align: right;"><span style="font-weight: bold; font-style: italic; ">Bếp trưởng nhà hàng Biển Nhớ</span></div></span>', '2012-07-02 00:00:00', '2012-07-02 00:00:00', 1, 'Cua trinh nữ hấp là sen', 0, 0, 'vi', ',6,56,', 'admin', '', 0);
INSERT INTO `web_info` VALUES (135, 'Các món chuyên đề', 'Cac-Mon-Chuyen-De-135', 'cac-mon-chuyen-de_1.jpg', 'Học viên có thể lựa chọn một trong các món ưa thích để theo học, được các Bếp Trưởng Nhà hàng lớn hướng dẫn theo phương pháp thực hành', '<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">1. Đặc điểm chương trình:&nbsp;</span>Học viên có thể lựa chọn một trong các món ưa thích để theo học, được các Bếp Trưởng Nhà hàng lớn hướng dẫn theo phương pháp thực hành</p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">2. Thời lượng học và thực hành:&nbsp;</span>3 giờ (Mỗi món)</p>\r\n\r\n<p><span style="color: rgb(255, 0, 0); "><span style="font-weight: bold; ">3. Thời gian học</span>: </span>tùy theo yêu cầu của Học viên</p>\r\n\r\n<p><span style="color: rgb(255, 0, 0); "><span style="font-weight: bold; ">4. Chứng chỉ hoàn tất khóa học</span>: </span>do Cơ Sở Dạy Nghề Ẩm Thực NetSpace cấp có giá trị toàn quốc và Hội đầu bếp Chuyên nghiệp Sài Gòn chứng nhận.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">5. Học phí:</span></p>\r\n\r\n<p>1.000.000đ - 5.000.000đ / món (Đã bao gồm chi phí nguyên vật liệu thực hành)</p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">6. Nội dung khóa học</span></p>\r\n\r\n<p>Học viên có thể lựa chọn một trong các chuyên đề sau:</p>\r\n\r\n<p>- Các món chay</p>\r\n\r\n<p>- Các món Hoa</p>\r\n\r\n<p>- Các món Âu</p>\r\n\r\n<p>- Cách làm chả lụa, chả Huế, Giò cháo quẩy</p>\r\n\r\n<p>- Các món chè</p>\r\n\r\n<p>- Cách làm bánh Tây</p>\r\n\r\n<p>- Vịt quay Bắc Kinh, Heo quay</p>\r\n\r\n<p>- Cắt tỉa, trang trí món ăn</p>\r\n', '2012-07-25 00:00:00', '2012-07-25 08:20:44', 0, 'Các món chuyên đề', 0, 0, 'vi', ',31,56,', 'admin', 'admin', 0);
INSERT INTO `web_info` VALUES (139, 'Chương trình đào tạo 100 phụ bếp miễn phí', 'Chuong-Trinh-Dao-Tao-100-Phu-Bep-Mien-Phi-139', 'Chuong-Trinh-Dao-Tao-100-Phu-Bep-Mien-Phi.jpg', 'Sau thời gian thực tập/ làm việc được từ 12 tháng trở lên. Nếu Học viên được bếp trưởng/Nhà hàng tín nhiệm đánh giá cao tính cần mẫn và yêu thích nghề bếp, sẽ được Nhà trường và Hội đầu bếp đào tạo tiếp tục miễn phí để trở thành Đầu bếp chuyên nghiệp tron', '\r\n<p style="text-align: left;">\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<img src="/upload/tin-tuc//truong-day-nghe-am-thuc.jpg" alt="Chương trình lớp Bếp Trưởng chuyên nghiệp" align="left" border="0px" /></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p style="text-align: right;">\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<img src="/upload/tin-tuc//hoi-dau-bep-chuyen-nghiep-sg.jpg" alt="Chương trình lớp Bếp Trưởng chuyên nghiệp" align="" border="0px" /></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(30, 144, 255); "><span style="font-weight: bold; ">I. </span><span style="font-weight: bold; ">Đơn vị tổ chức</span></span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Trường Dạy Nghề Ẩm Thực NETSPACE</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Hội Đầu Bếp Chuyên Nghiệp SÀI GÒN</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>&nbsp;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(30, 144, 255); "><span style="font-weight: bold; ">II. </span><span style="font-weight: bold; ">Mục tiêu đào tạo</span></span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Học viên được truyền đạt kỷ năng chuyên môn của người Phụ bếp</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Sau khi học xong, học viên có đủ kỹ năng của 1 phụ bếp chuyên nghiệp, làm việc được ngay tại bộ phận bếp trong nhà hàng, khách sạn,..</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Sau thời gian thực tập/ làm việc được từ 12 tháng trở lên. Nếu Học viên được bếp trưởng/Nhà hàng tín nhiệm đánh giá cao tính cần mẫn và yêu thích nghề bếp, sẽ được Nhà trường và Hội đầu bếp đào tạo tiếp tục miễn phí để trở thành Đầu bếp chuyên nghiệp trong 3 tháng.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>&nbsp;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(30, 144, 255); "><span style="font-weight: bold; ">III. </span><span style="font-weight: bold; ">Tiêu chuẩn tuyển sinh</span></span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Trình độ học vấn: tốt nghiệp từ trung học cơ sở trở lên;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Độ tuổi từ 18 đến 22;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Qua kỳ thi tuyển của Trường và Hội đầu bếp Chuyên Nghiệp Sài Gòn (Ưu tiên học viên có sự giới thiệu của Hội Viên SPC);</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Chấp nhận các khoản hợp đồng ký kết với Trường.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>&nbsp;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(30, 144, 255); "><span style="font-weight: bold; ">IV. </span><span style="font-weight: bold; ">Thời gian đào tạo</span></span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- 6 tuần, tuần học 3 buổi.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>&nbsp;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(30, 144, 255); "><span style="font-weight: bold; ">V. </span><span style="font-weight: bold; ">Chương trình đào tạo</span></span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(128, 128, 0); "><span lang="VI" style="font-weight: bold; ">A. </span><span style="font-weight: bold; ">Lý thuyết: </span></span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- An toàn vệ sinh thực phẩm ( tổ chức bếp, lưu trữ thực phẩm, ngộ độc do thực phẩm có chất độc, bị thiu biến chất, độc tố vi sinh vật, hóa chất,...) </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Lý thuyết dinh dưỡng ( Phân loại, đánh giá chất lượng thực phẩm, phương pháp bảo quản thực phẩm, tìm hiểu vi sinh vật, chất dinh dưỡng cần thiết cho cơ thể,...) </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Văn hóa ẩm thực ( Khẩu vị món ăn 3 miền, Xây dựng thực đơn, phương pháp sử dụng gia vị, môi trường,...) </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Các kiến thức cơ bản của nghiệp vụ bếp (nội quy bếp, PCCC, Ca làm việc, dụng cụ bếp, vận hành thiết bị nhà bếp,...)</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(128, 128, 0); "><span lang="VI" style="font-weight: bold; ">B. </span><span style="font-weight: bold; ">Thực hành:</span></span><br />\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<br />\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	- Kỹ năng thớt, chảo, sử dụng dao (3 buổi)</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Sơ chế rau củ, thịt gia cầm, thịt gia súc: tôm, mực,.. (1 buổi).</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Pha chế các loại nước chấm (1 buổi). </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Kỹ năng cắt tỉa rau củ quả trang trí món ăn: tỉa khối các loại củ, trang trí viền dĩa, tỉa cơ bản từ ớt, cà chua, dưa leo (3 buổi)</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Thực hành chế biến 1 thực đơn tiệc VN (Tổng hợp các món 3 miền) (1 buổi).</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Thực hành chế biến 2 món ăn thuần túy Trung Hoa (Xào, quay) (1 buổi)..</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Thực hành chế biến 2 món ăn thuần túy Âu (Súp, Xà lách) (1 buổi).</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>&nbsp;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(30, 144, 255); "><span style="font-weight: bold; ">VI. </span><span style="font-weight: bold; ">Chứng chỉ tốt nghiệp</span></span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Học viên được cấp bằng chứng nhận học nghề của Trường Ẩm Thực NETSPACE và Hội Đầu Bếp Chuyên Nghiệp Sài Gòn.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>&nbsp;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(30, 144, 255); "><span style="font-weight: bold; ">VII. </span><span style="font-weight: bold; ">Hồ sơ đăng ký</span></span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>1. 01 Đơn đăng ký tham gia Chương trình (Theo mẫu)</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>2. 01 Sơ yếu lý lịch, có xác nhận tại địa phương;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>3. 01 Bản sao CMND(Có sao y bản chính);</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>4. 01 Bản sao Bằng tốt nghiệp phổ thông cơ sở hoặc phổ thông trung học(Có sao y bản chính) </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>&nbsp;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(30, 144, 255); "><span style="font-weight: bold; ">VIII. </span><span style="font-weight: bold; ">Thời gian đăng ký</span></span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0); ">5/9/2012 - 15/10/2012</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>&nbsp;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(30, 144, 255); "><span style="font-weight: bold; ">IX. </span><span style="font-weight: bold; ">Địa điểm đăng ký</span></span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(128, 128, 0); ">1. Trường Ẩm Thực NetSpace</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p> 30 Nguyễn Huy Tự, P.Đakao, Quận 1, Tp.HCM</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p> Tel: (08)6291 2698 , Hotline: 0903 646893</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(128, 128, 0); ">2. Văn phòng Hội đầu bếp CN Sài Gòn</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p> 140 Nguyễn Đình Chiểu, Quận 3, Tp.HCM</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p> Tel: (08) 3822 9306</p>', '2012-09-06 00:00:00', '2012-09-06 00:00:00', 0, 'Chương trình đào tạo 100 phụ bếp miễn phí', 0, 0, 'vi', ',31,56,', 'admin', '', 0);
