INSERT INTO `web_info` VALUES (145, 'Chương trình dạy cắt tỉa chuyên nghiệp', 'Chuong-Trinh-Day-Cat-Tia-Chuyen-Nghiep-145', 'day-cat-tia-chuyen-nghiep.jpg', 'Chọn dao tỉa ,dụng cụ và cách cầm dao tỉa. Cắt cơ bản, cắt đồ xào. Tỉa bông hồng,bông súng, bông soáy và bông ly', '\r\n<p style="text-align: left;">\r\n	\r\n	\r\n	\r\n	\r\n	<img src="/upload/tin-tuc//truong-day-nghe-am-thuc.jpg" alt="Chương trình lớp Bếp Trưởng chuyên nghiệp" align="left" border="0px" /></p>\r\n\r\n\r\n\r\n\r\n<p style="text-align: right;">\r\n	\r\n	\r\n	\r\n	\r\n	<img src="/upload/tin-tuc//hoi-dau-bep-chuyen-nghiep-sg.jpg" alt="Chương trình lớp Bếp Trưởng chuyên nghiệp" align="" border="0px" /></p>\r\n\r\n\r\n\r\n\r\n<p style="text-align: center;"><span style="font-weight: bold; "><span style="font-size: 18pt; color: rgb(255, 0, 0); "><span lang="VI" style="font-size: 18pt; ">Khai giảng </span>19-20<span lang="VI" style="font-size: 18pt; ">/</span>11<span lang="VI" style="font-size: 18pt; ">/2012</span></span></span></p>\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(30, 144, 255); font-weight: bold; ">I. </span><span style="font-weight: bold; color: rgb(30, 144, 255); ">LỚP CẮT TỈA CĂN BẢN:</span></p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; ">Nội dung chương trình</span></p>\r\n\r\n\r\n\r\n\r\n<ol style="">\r\n	\r\n	\r\n	\r\n	\r\n	<li>Chọn dao tỉa ,dụng cụ và cách cầm dao tỉa</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Cắt cơ bản, cắt đồ xào</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa bông hồng,bông súng, bông soáy và bông ly</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa bướm,tôm,cua,và chim cò bằng cà rốt, củ cải,dưa leo</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa bông dưa hấu cơ bản- 3 loại bông</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa bông dưa hấu kỹ thuật- các loại hoa văn</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa vỏ dưa hấu</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa lồng đèn dưa hấu</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa bông trên quả đu đủ (bông phù dung)</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>10.<span class="Apple-tab-span" style="white-space:pre"></span>Tỉa thố bí, bình hoa</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>11.<span class="Apple-tab-span" style="white-space:pre"></span>Tỉa giỏ hoa trên bí</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa con vật kỹ thuật (gà ,heo,đại bàng…)</li>\r\n\r\n\r\n\r\n\r\n</ol>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; ">Thời gian học: </span>1 tháng và học vào các buổi thứ 2,4,6 hàng tuần.</p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; ">Học phí : </span>2.000.000đ (Hai triệu đồng), Không bao gồm nguyên liệu thực tập.</p>\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(30, 144, 255); font-weight: bold; ">II. </span><span style="font-weight: bold; color: rgb(30, 144, 255); ">LỚP CẮT TỈA NÂNG CAO:</span></p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; ">Nội dung chương trình</span></p>\r\n\r\n\r\n\r\n\r\n<ol style="">\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa chim phụng</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa con rồng</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa hình người</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa rùa - Lão quy</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa lân ,sư tử</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa phúc lộc thọ</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Tỉa mặt người (Halloween)</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Cách trang trí dĩa thức ăn</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Cách trang trí tiệc buffer</li>\r\n	\r\n	\r\n	\r\n	\r\n	<li>Cách setup bàn tiệc</li>\r\n\r\n\r\n\r\n\r\n</ol>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; ">Thời gian học: </span>1 tháng và học vào các buổi thứ 3,5,7 hàng tuần</p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; ">Học phí: </span>2.500.000đ (Hai triệu năm trăm ngàn đồng), Không bao gồm nguyên liệu thực tập<br />\r\n	\r\n	\r\n	\r\n	<br />\r\n	\r\n	\r\n	\r\n	</p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(30, 144, 255); ">Giảng viên </span></p>\r\n\r\n\r\n\r\n\r\n<p>1. <span class="Apple-tab-span" style="white-space:pre"></span>Thầy <span style="font-weight: bold; ">NGUYỄN VĂN LẬP</span>: huy chương vàn