INSERT INTO `web_info` VALUES (161, 'Chương trình đào tạo Mở Nhà Hàng', 'Chuong-Trinh-Dao-Tao-Mo-Nha-Hang-161', 'Chuong-Trinh-Dao-Tao-Lam-Chu-Nha-Hang-2.jpg', 'Ngày khai giảng: <font color=red><b>Hàng tuần</b></font>', '\r\n<p style="text-align: center;"><span style="font-weight: bold;">Ngày&nbsp;</span><span style="font-weight: bold;">khai giảng:&nbsp;</span><span style="font-weight: bold; color: rgb(255, 20, 147);">Hàng tuần</span></p>\r\n<p style="text-align: center;"><a href="http://www.dayamthuc.vn/dang-ky-truc-tuyen.html">\r\n		<img src="http://www.dayamthuc.vn/upload/tin-tuc/dang-ky-truc-tuyen.gif" alt="đăng ký trực tuyến" align="" border="0px" /></a></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">1. Đặc điểm chương trình: </span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Chủ Nhà hàng đòi hỏi phải có kiến thức về quản lý, có nghiệp vụ chuyên môn, có nhiều kỹ năng làm việc và am hiểu về ẩm thực. </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Chương trình đào tạo làm Chủ Nhà hàng được Trường NetSpace thiết kế dành riêng cho Các bạn yêu thích kinh doanh Ẩm thực theo mô hình Nhà hàng. Chương trình chú trọng thực hành thực tế và kinh nghiệm thực tiễn điều hành mô hình Nhà hàng và học thực tế tại các các Nhà hàng là Đối tác của Trường NetSpace. Điều này sẽ giúp cho Học viên học được rất nhiều và có thể tự tin mở Nhà hàng với khả năng thành công cao. </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Giảng viên là những Nhà quản lý hàng đầu về Nhà hàng tại Việt Nam.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">2. Thời gian học:</span> 2 tháng ( 1 tháng học tại Trường và 1 tháng thực tập quản lý thực tế tại mô hình Nhà hàng</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">3. Nội dung chương trình đào tạo:</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">Phần 1: 1 tháng học tại Trường (12 buổi)</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Mô tả nghề Quản lý Nhà hàng, các yêu cầu về kiến thức và kỹ năng nghề Quản lý Nhà hang, các kỹ năng mềm dành cho Quản lý Nhà hàng </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Xây dựng Sơ đồ tổ chức và bố trí nhân sự Nhà hàng, xây dựng định biên nhân sự </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Mô tả công việc các bộ phận </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Quản lý mối quan hệ giữa các bộ phận </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Nội qui &amp; quy chế hoạt động Nhà Hàng </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Kiểm soát &amp; quản lý tài chính Nhà Hàng </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Kiến thức căn bản về bộ phận Phục vụ </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Kiến thức căn bản về bộ phận Pha chế </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Kiến thức căn bản về bộ phân Bếp </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Kiến thức căn bản về văn hóa ẩm thực, an toàn, vệ sinh thực phẩm </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Marketing Nhà hàng </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Chăm sóc khách hàng &amp; kỹ năng giải quyết tình huống </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">Phần 2: Thực tập thực tế tại Nhà hàng</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- <span class="Apple-tab-span" style="white-space:pre"></span>Tìm hiểu sơ đồ tổ chức và chức năng của các bộ phận, mối quan hệ giữa các bộ phận;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- <span class="Apple-tab-span" style="white-space:pre"></span>Phương pháp điều hành, báo cáo;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- <span class="Apple-tab-span" style="white-space:pre"></span>Nội quy, quy chế hoạt động;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- <span class="Apple-tab-span" style="white-space:pre"></span>Thực tập tại các bộ phận khác nhau: bếp, pha chế, hành chính-nhân sự, tài chính-kế toán, marketing...</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- <span class="Apple-tab-span" style="white-space:pre"></span>Phương pháp điều hành các bộ phận;</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- <span class="Apple-tab-span" style="white-space:pre"></span>Marketing Nhà hàng; </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- <span class="Apple-tab-span" style="white-space:pre"></span>Kiểm soát chi phí, quản lý kế toán, tài chính.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Ngoài ra, Học viên sẽ được hổ trợ tư vấn miễn phí việc chọn địa điểm kinh doanh, thiết kế, xây dựng, sửa chữa, nơi mua trang thiết bị, bàn ghế, cách triển khai 1 nhà hàng . </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">4. Học phí</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Phần 1: Trang bị các kiến thức và kỹ năng quản lý Nhà hàng : 10 triệu</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>- Phần 2: Thực tập thực tế tại mô hình Nhà hàng: 5 triệu</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Học phí trọn gói: 15 triệu ( Đã bao gồm 12 buổi học, 1 tháng thực tập và chi phí tư vấn)</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">5. Chứng chỉ hoàn tất khóa học: </span>do Trường Ẩm Thực NetSpace cấp có giá trị toàn quốc </p>\r\n<p style="text-align: center;"><a href="http://www.dayamthuc.vn/dang-ky-truc-tuyen.html">\r\n		<img src="http://www.dayamthuc.vn/upload/tin-tuc/dang-ky-truc-tuyen.gif" alt="đăng ký trực tuyến" align="" border="0px" /></a></p>', '2013-01-13 00:00:00', '2013-01-13 00:00:00', 0, 'Chuong Trinh Dao Tao, Mo Nha Hang', 0, 0, 'vi', ',4,56,', 'admin', '', 0);
INSERT INTO `web_info` VALUES (162, 'Bếp chính bếp Việt nhà hàng', 'Bep-Chinh-Bep-Viet-Nha-Hang-162', 'Bep-Chinh-Bep-Viet-Nha-Hang-2.JPG', 'Khai giảng: <br /><font color=red><b>14:00 - 17:00 ngày 09/04/2013</b></font>', '\r\n<p style="text-align: center;"><span style="font-weight: bold;">Khai giảng:&nbsp;</span><span style="font-weight: bold;"><span style="color: rgb(255, 20, 147);">14:00 - 17:00 ngày&nbsp;</span></span><span style="font-weight: bold; color: rgb(255, 20, 147);">09/04/2013</span></p>\r\n\r\n<div style="text-align: center;"><a href="http://www.dayamthuc.vn/dang-ky-truc-tuyen.html">\r\n		\r\n		<img src="http://www.dayamthuc.vn/upload/tin-tuc/dang-ky-truc-tuyen.gif" alt="đăng ký trực tuyến" align="" border="0px" /></a></div>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">1. Đặc điểm chương trình:</span> Ẩm thực Việt Nam được cả thế giới biết đến nhờ vào tài năng của những đầu bếp Việt Nam tài hoa đã làm nên những món ăn thuần Việt kết hợp với những tinh hoa của các món ăn Phương Tây và Phương Đông.</p>\r\n\r\n<p>Chương trình Bếp Việt Nhà Hàng được Trường Ẩm Thực NetSpace xây dựng nhằm đào tạo những Đầu bếp Chuyên nghiệp có kỹ năng tốt để làm việc tại bộ phận bếp của các Nhà hàng, Khách sạn, Khu Du lịch tại Việt Nam.</p>\r\n\r\n<p>Tham gia Chương trình này, Học viên được học với các Bếp Trưởng hàng đầu tại Việt Nam, được thực tập và có nhiều cơ hội việc làm tại các Nhà hàng, Khách sạn, Khu Du Lịch lớn với công việc ổn định, môi trường làm việc chuyên nghiệp, thu nhập cao.</p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">2. Thời gian học:</span> 4 tháng </p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">3. Chứng chỉ hoàn tất khóa học:</span> do Trường Ẩm Thực NetSpace cấp có giá trị toàn quốc và Hội đầu bếp Chuyên nghiệp Sài Gòn chứng nhận.</p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">4. Học phí:</span></p>\r\n\r\n<p>12.000.000 đ/khóa (Đã bao gồm chi phí nguyên vật liệu thực hành và đồng phục bếp)</p>\r\n\r\n<p>(Có thể đóng 2 lần, mỗi lần 6.000.000đ)</p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">5. Nội dung khóa học</span></p>\r\n\r\n<p><span style="font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">Phần 1: Kiến thức và kỹ năng</span></p>\r\n\r\n<blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;">\r\n	\r\n	<p>1.<span class="Apple-tab-span" style="white-space:pre"></span>Kỹ năng thớt chảo và kiểm tra</p>\r\n	\r\n	<p>2.<span class="Apple-tab-span" style="white-space:pre"></span>Kỹ năng cắt tỉa, trang trí món ăn</p>\r\n	\r\n	<p>3.<span class="Apple-tab-span" style="white-space:pre"></span>Kỹ năng trong bếp Việt</p>\r\n	\r\n	<p>4.<span class="Apple-tab-span" style="white-space:pre"></span>Các quy trình làm việc, các kỹ năng tổ chức, làm việc trong bếp </p>\r\n	\r\n	<p>5.<span class="Apple-tab-span" style="white-space:pre"></span>Vệ sinh an toàn thực phẩm</p>\r\n	\r\n	<p>6.<span class="Apple-tab-span" style="white-space:pre"></span>Phòng cháy chữa cháy</p>\r\n	\r\n	<p>7.<span class="Apple-tab-span" style="white-space:pre"></span>Văn hóa ẩm thực</p>\r\n	\r\n	<p>8.<span class="Apple-tab-span" style="white-space:pre"></span>Kỹ thuật bảo quản, sơ chế rau</p>\r\n	\r\n	<p>9.<span class="Apple-tab-span" style="white-space:pre"></span>Kỹ thuật nấu các loại nước dùng cơ bản</p></blockquote>\r\n\r\n<p><span style="font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">Phần 2: Kỹ thuật chế biến các món ăn Việt Nam</span><span class="Apple-tab-span" style="white-space:pre"></span></p>\r\n\r\n<p><span style="font-weight: bold;">I. Các món ăn sáng</span></p>\r\n\r\n<blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;">\r\n	\r\n	<p>1.<span class="Apple-tab-span" style="white-space:pre"></span>Phở bò</p>\r\n	\r\n	<p>2.<span class="Apple-tab-span" style="white-space:pre"></span>Bún riêu</p>\r\n	\r\n	<p>3.Trứng ốp-la</p></blockquote>\r\n\r\n<p><span style="font-weight: bold;">II. Cơm Việt Nam</span><span class="Apple-tab-span" style="white-space:pre"></span></p>\r\n\r\n<blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;">\r\n	\r\n	<p>1.<span class="Apple-tab-span" style="white-space:pre"></span>Kỹ thuật nấu cơm</p>\r\n	\r\n	<p>2.<span class="Apple-tab-span" style="white-space:pre"></span>Rau muống xào tỏi</p>\r\n	\r\n	<p>3.Trứng chiên thịt</p>\r\n	\r\n	<p>4.<span class="Apple-tab-span" style="white-space:pre"></span>Canh cua rau đay</p>\r\n	\r\n	<p>5.<span class="Apple-tab-span" style="white-space:pre"></span>Rau luộc thập cẩm kho quẹt</p>\r\n	\r\n	<p>6.Cá rô kho tộ</p>\r\n	\r\n	<p>7.Thịt kho tàu</p>\r\n	\r\n	<p>8.<span class="Apple-tab-span" style="white-space:pre"></span>Canh chua cá bông lau</p></blockquote>\r\n\r\n<p><span style="font-weight: bold;">III. Các món khai vị</span></p>\r\n\r\n<blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;">\r\n	\r\n	<p>1.<span class="Apple-tab-span" style="white-space:pre"></span>Gỏi ngó sen</p>\r\n	\r\n	<p>2.<span class="Apple-tab-span" style="white-space:pre"></span>Gỏi bò bóp thấu</p>\r\n	\r\n	<p>3.<span class="Apple-tab-span" style="white-space:pre"></span>Chả giò hải sản</p>\r\n	\r\n	<p>4.<span class="Apple-tab-span" style="white-space:pre"></span>Gỏi cuốn</p>\r\n	\r\n	<p>5.Càng cua Bách Hoa</p>\r\n	\r\n	<p>6.Súp sơn hào hải vị</p>\r\n	\r\n	<p>7.Chả giò sen</p>\r\n	\r\n	<p>8.Tôm lăn bột</p>\r\n	\r\n	<p>9.Mực chiên giòn</p></blockquote>\r\n\r\n<p><span style="font-weight: bold;">IV. Các món nướng</span></p>\r\n\r\n<blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;">\r\n	\r\n	<p>1.<span class="Apple-tab-span" style="white-space:pre"></span>Sườn chìa mật ong, mè</p>\r\n	\r\n	<p>2.<span class="Apple-tab-span" style="white-space:pre"></span>Heo nướng xiên (BBQ)</p>\r\n	\r\n	<p>3.<span class="Apple-tab-span" style="white-space:pre"></span>Bò nướng lá lốt</p>\r\n	\r\n	<p>4.<span class="Apple-tab-span" style="white-space:pre"></span>Tôm, mực, gà nướng muối ớt </p>\r\n	\r\n	<p>5.<span class="Apple-tab-span" style="white-space:pre"></span>Gà nướng niêu</p>\r\n	\r\n	<p>6.<span class="Apple-tab-span" style="white-space:pre"></span>Cá Sapa nướng giấy bạc</p>\r\n	\r\n	<p>7.<span class="Apple-tab-span" style="white-space:pre"></span>Cá lóc nướng trui</p></blockquote>\r\n\r\n<p><span style="font-weight: bold;">V. Các món hấp</span></p>\r\n\r\n<blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;">\r\n	\r\n	<p>1.<span class="Apple-tab-span" style="white-space:pre"></span>Gà hấp muối</p>\r\n	\r\n	<p>2.<span class="Apple-tab-span" style="white-space:pre"></span>Cá điêu hồng chưng tương</p>\r\n	\r\n	<p>3.<span class="Apple-tab-span" style="white-space:pre"></span>Mực hấp hành ,gừng</p>\r\n	\r\n	<p>4.<span class="Apple-tab-span" style="white-space:pre"></span>Ốc nhồi thịt hấp gừng (sả)</p>\r\n	\r\n	<p>5.<span class="Apple-tab-span" style="white-space:pre"></span>Cá lóc hấp bầu</p></blockquote>\r\n\r\n<p><span style="font-weight: bold;">VI. Các món chiên, xào</span></p>\r\n\r\n<blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;">\r\n	\r\n	<p>1.<span class="Apple-tab-span" style="white-space:pre"></span>Cải xào dầu hào</p>\r\n	\r\n	<p>2.<span class="Apple-tab-span" style="white-space:pre"></span>Tôm Hoàng Kim</p>\r\n	\r\n	<p>3.<span class="Apple-tab-span" style="white-space:pre"></span>Bò beef steak</p>\r\n	\r\n	<p>4.<span class="Apple-tab-span" style="white-space:pre"></span>Bò lúc lắc</p>\r\n	\r\n	<p>5.<span class="Apple-tab-span" style="white-space:pre"></span>Cá tai tượng chiên xù</p>\r\n	\r\n	<p>6.<span class="Apple-tab-span" style="white-space:pre"></span>Cua rang me</p>\r\n	\r\n	<p>7.<span class="Apple-tab-span" style="white-space:pre"></span>Giò heo chiên giòn</p>\r\n	\r\n	<p>8.<span class="Apple-tab-span" style="white-space:pre"></span>Miến xào cua</p>\r\n	\r\n	<p>9.<span class="Apple-tab-span" style="white-space:pre"></span>Mì xào giòn</p>\r\n	\r\n	<p>10.<span class="Apple-tab-span" style="white-space:pre"></span>Sườn xào tàu xì</p>\r\n	\r\n	<p>11.<span class="Apple-tab-span" style="white-space:pre"></span>Cơm chiên gà x&eacute; cá mặn</p>\r\n	\r\n	<p>12.<span class="Apple-tab-span" style="white-space:pre"></span>Cơm chiên Dương Châu</p>\r\n	\r\n	<p>13.<span class="Apple-tab-span" style="white-space:pre"></span>Cánh gà chiên nước mắm</p>\r\n	\r\n	<p>14.<span class="Apple-tab-span" style="white-space:pre"></span>Bún gạo xào thập cẩm</p></blockquote>\r\n\r\n<p><span style="font-weight: bold;">VII. Các món hầm, tiềm, lẩu</span></p>\r\n\r\n<blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;">\r\n	\r\n	<p>1.<span class="Apple-tab-span" style="white-space:pre"></span>Gà rôti</p>\r\n	\r\n	<p>2.<span class="Apple-tab-span" style="white-space:pre"></span>Cari gà</p>\r\n	\r\n	<p>3.<span class="Apple-tab-span" style="white-space:pre"></span>Bò nấu lagu</p>\r\n	\r\n	<p>4.<span class="Apple-tab-span" style="white-space:pre"></span>Bao tử hầm tiêu</p>\r\n	\r\n	<p>5.<span class="Apple-tab-span" style="white-space:pre"></span>Lẩu gà tiềm ớt hiểm</p>\r\n	\r\n	<p>6.<span class="Apple-tab-span" style="white-space:pre"></span>Lẩu hải sản chua cay</p>\r\n	\r\n	<p>7.<span class="Apple-tab-span" style="white-space:pre"></span>Lẩu cá thác lác khổ qua (xí quách)</p>\r\n	\r\n	<p>8.<span class="Apple-tab-span" style="white-space:pre"></span>Lẩu nấm Hoa</p>\r\n	\r\n	<p>9.<span class="Apple-tab-span" style="white-space:pre"></span>Cháo gà x&eacute; phai rau râm</p>\r\n	\r\n	<p>10.<span class="Apple-tab-span" style="white-space:pre"></span>Cháo cá miền Tây</p></blockquote>\r\n\r\n<p><span style="font-weight: bold;">VIII. Các món tráng miệng</span></p>\r\n\r\n<blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;">\r\n	\r\n	<p>1.<span class="Apple-tab-span" style="white-space:pre"></span>Bánh bông lan</p>\r\n	\r\n	<p>2.<span class="Apple-tab-span" style="white-space:pre"></span>Bánh su kem</p>\r\n	\r\n	<p>3.<span class="Apple-tab-span" style="white-space:pre"></span>