INSERT INTO `web_info` VALUES (163, 'Bếp chính bếp Hoa nhà hàng', 'Bep-Chinh-Bep-Hoa-Nha-Hang-163', 'Bep-Chinh-Bep-Hoa-Nha-Hang-2.jpg', 'Khai giảng: <br /><font color=red><b>14:00 - 17:00 ngày 17/04/2013</b></font>', '\r\n<p style="text-align: center;"><span style="font-weight: bold;">Khai giảng:&nbsp;</span><span style="font-weight: bold;"><span style="color: rgb(255, 20, 147);">14:00 - 17:00 ngày&nbsp;</span></span><span style="font-weight: bold; color: rgb(255, 20, 147);">17/04/2013</span>\r\n	</p>\r\n\r\n<div style="text-align: center;"><a href="http://www.dayamthuc.vn/dang-ky-truc-tuyen.html">\r\n		\r\n		<img src="http://www.dayamthuc.vn/upload/tin-tuc/dang-ky-truc-tuyen.gif" alt="đăng ký trực tuyến" align="" border="0px" /></a></div>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">1. Đặc điểm chương trình:</span> Ẩm thực Trung Hoa có từ lâu đời,với nhiều món ăn ngon, rất đa dạng và được trang trí đẹp mắt. Các Nhà hàng, Quán ăn Trung Hoa có mặt khắp nơi trên thế giới, với những khu phố ẩm thực Trung Hoa sầm uất, rất nhiều khách hàng ưa chuộng. </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Chương trình Bếp Hoa Nhà Hàng được xây dựng bởi các Đầu bếp nổi tiếng người Hoa. Chương trình sẽ đào tạo những Đầu bếp Chuyên nghiệp nấu các món Hoa, có kỹ năng tốt để làm việc tại bộ phận bếp Hoa của các Nhà hàng, Khách sạn, Khu Du lịch.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>Tham gia Chương trình này, Học viên được học với các Bếp Trưởng người Hoa hàng đầu tại Việt Nam, được thực tập và có nhiều cơ hội việc làm tại các Nhà hàng, Khách sạn, Khu Du Lịch lớn với công việc ổn định, môi trường làm việc chuyên nghiệp, thu nhập cao.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">2. Thời gian học:</span> 4 tháng </p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">3. Chứng chỉ hoàn tất khóa học:</span> do Trường Ẩm Thực NetSpace cấp có giá trị toàn quốc và Hội đầu bếp Chuyên nghiệp Sài Gòn chứng nhận.</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">4. Học phí:</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>12.000.000 đ/khóa (Đã bao gồm chi phí nguyên vật liệu thực hành và đồng phục bếp)</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p>(Có thể đóng 2 lần, mỗi lần 6.000.000đ)</p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">5. Nội dung khóa học</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">Phần 1: Kiến thức và kỹ năng</span></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;">\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<p>1.<span class="Apple-tab-span" style="white-space:pre"></span>Kỹ năng thớt chảo</p>\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<p>2.<span class="Apple-tab-span" style="white-space:pre"></span>Phương pháp cắt tỉa, trang trí món ăn</p>\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<p>3.<span class="Apple-tab-span" style="white-space:pre"></span>Các kỹ năng trong bếp Hoa</p>\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<p>4.<span class="Apple-tab-span" style="white-space:pre"></span>Các quy trình làm việc, các kỹ năng tổ chức, làm việc trong bếp </p>\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<p>5.<span class="Apple-tab-span" style="white-space:pre"></span>Vệ sinh an toàn thực phẩm</p>\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<p>6.<span class="Apple-tab-span" style="white-space:pre"></span>Phòng cháy chữa cháy</p>\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<p>7.<span class="Apple-tab-span" style="white-space:pre"></span>Văn hóa ẩm thực Trung Hoa</p>\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	\r\n	<p>8.<span class="Apple-tab-span" style="white-space:pre"></span>Kỹ thuật chế biến rau</p>\r\