INSERT INTO `web_info` VALUES (185, 'Dạy nghề Heo quay, Vịt quay, Gà quay', 'Day-Nghe-Heo-Quay-Vit-Quay-Ga-Quay-185', 'Chuong-Trinh-Day-Nghe-Heo-Quay-Vit-Quay-Ga-Quay-2.jpg', 'Khai giảng: <font color=red><b>Hàng tuần</b></font>', '\r\n<p style="text-align: center;">&nbsp;<span style="font-weight: bold;">Khai giảng: </span><span style="font-weight: bold; color: rgb(255, 20, 147);">Hàng tuần</span></p>\r\n\r\n\r\n\r\n\r\n<div style="text-align: center;"><a href="http://www.dayamthuc.vn/dang-ky-truc-tuyen.html">\r\n		\r\n		\r\n		\r\n		\r\n		<img src="http://www.dayamthuc.vn/upload/tin-tuc/dang-ky-truc-tuyen.gif" alt="đăng ký trực tuyến" align="" border="0px" /></a></div>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold;"><span style="color: rgb(255, 0, 0);">1. Đặc điểm chương trình:</span></span><span style="font-weight: bold;"></span>đây là Chương trình <span lang="VI">đào tạo nghề yêu cầu các kỹ năng và trình độ cao. Các Bạn tham gia học Chương trình này sẽ có đầy đủ khả năng mở lò quay, tiệm quay heo, quay vịt, quay gà.</span></p>\r\n\r\n\r\n\r\n\r\n<p><span lang="VI"><span style="font-weight: bold;">Giảng viên</span> là những Đầu bếp, Chuyên Gia gốc Hoa, có kinh nghiệm lâu năm.</span></p>\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(255, 0, 0);"><span style="font-weight: bold;">2. Thời gian học</span>: </span><span lang="VI">2</span> buổi học thực tế tại Trường <span lang="VI">cho mỗi món quay và được tư vấn miễn phí khi mở tiệm.</span></p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">3. Nội dung chương trình đào tạo: </span>\r\n	\r\n	\r\n	\r\n	\r\n	<spanlang="vi" style="font-weight: bold;"></spanlang="vi"></p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold;">Buổi 1:</span><span lang="VI">Giảng viên hướng dẫn tỉ mỉ bí quyết quay heo, quay vịt, quay gà</span><span lang="VI"></span><span lang="VI">từ khâu sơ chế nguyên liệu, ướp tẩm hương, vị, đến công đoạn quay và ra thành phẩm.</span><span lang="VI"></span>\r\n	\r\n	\r\n	\r\n	\r\n	<spanlang="vi"></spanlang="vi"></p>\r\n\r\n\r\n\r\n\r\n<p><span lang="VI"><span style="font-weight: bold;">Buổi 2:</span> Học viên tự thực hiện các công việc từ sơ chế nguyên liệu đến hoàn tất thành phẩm dưới sự giám sát, hổ trợ của Giảng viên.</span></p>\r\n\r\n\r\n\r\n\r\n<p><span lang="VI">Ngoài ra, </span>Học viên sẽ được tư vấn <span lang="VI">miễn phí </span>việc chọn địa điểm kinh doanh, nơi mua <span lang="VI">lò quay, dụng</span> cụ, cách triển khai 1 <span lang="VI">lò quay, tiệm quay</span><span lang="VI"></span><span lang="VI">.</span></p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">4. Học phí</span></p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; font-style: italic;">- <span lang="VI">Heo quay (nguyên con): 1</span>6<span lang="VI"> triệu </span></span></p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; font-style: italic;"><span lang="VI">- Heo quay (miếng): </span>12\r\n		\r\n		\r\n		\r\n		\r\n		<spanlang="vi"> triệu</spanlang="vi"></span></p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; font-style: italic;"><span lang="VI">- Vịt quay: 1</span>2<span lang="VI">triệu</span></span></p>\r\n\r\n\r\n\r\n\r\n<p><span style="font-weight: bold; font-style: italic;"><span lang="VI">- Gà nướng, gà quay: 1</span>2\r\n		\r\n		\r\n		\r\n		\r\n		<spanlang="vi"> triệu</spanlang="vi"></span></p>\r\n\r\n\r\n\r\n\r\n<p><span lang="VI">(Học phí này đã bao gồm toàn bộ nguyên liệu thực hành)</span></p>\r\n\r\n\r\n\r\n\r\n<p><span lang="VI">Nếu Học viên đăng ký học từ 2 món quay trở lên, học phí sẽ được <span style="font-weight: bold; color: rgb(255, 0, 0);">ưu đãi giảm 2</span></span><span style="font-weight: bold; color: rgb(255, 0, 0);">0</span><span lang="VI"><span style="font-weight: bold; color: rgb(255, 0, 0);">%</span> trên tổng học phí.</span></p>\r\n\r\n\r\n\r\n\r\n<p><span lang="VI"></span></p>\r\n\r\n\r\n\r\n\r\n<p><span lang="VI"></span></p>\r\n\r\n\r\n\r\n\r\n<p><span style="color: rgb(255, 0, 0);"><span style="font-weight: bold;">5. </span><span style="font-weight: bold;">Chứng chỉ hoàn tất khóa học</span>:</span><span style="font-weight: bold;">do </span>\r\n	\r\n	\r\n	\r\n	\r\n	<spanlang="vi" style="font-weight: bold;">Trường <span style="font-weight: bold;">Ẩm Thực NetSpace cấp có giá trị toàn quốc và Hội đầu bếp Chuyên nghiệp Sài Gòn chứng nhận.</span></spanlang="vi"></p>\r\n\r\n\r\n\r\n\r\n<p style="text-align: center;">\r\n	\r\n	\r\n	\r\n	\r\n	<img src="/upload/tin-tuc/Chuong-Trinh-Day-Nghe-Heo-Quay-Vit-Quay-Ga-Quay.jpg" alt="Chương trình dạy nghề Heo quay, Vịt quay, Gà quay" align="" border="0px" /></p>\r\n\r\n\r\n\r\n\r\n<p style="text-align: center;"><br />\r\n	\r\n	\r\n	\r\n	\r\n	</p>\r\n\r\n\r\n\r\n\r\n<div style="text-align: left;"><a href="http://www.dayamthuc.vn/dang-ky-truc-tuyen.html" style="text-align: center;">\r\n		\r\n		\r\n		\r\n		\r\n		<img src="http://www.dayamthuc.vn/upload/tin-tuc/dang-ky-truc-tuyen.gif" alt="đăng ký trực tuyến" align="" border="0px" /></a></div>\r\n\r\n\r\n\r\n\r\n<p>&nbsp;</p>', '2013-03-22 00:00:00', '2013-03-22 00:00:00', 0, 'Day Nghe Heo Quay, Vit Quay, Ga Quay', 0, 0, 'vi', ',4,56,', 'admin', '', 0);
INSERT INTO `web_info` VALUES (186, 'Món Huế', 'mon-hue-186', 'Day-Mon-Hue.jpg', 'Xứ Huế được mệnh danh là vùng đất Kinh đô trước đây của Việt Nam với những câu hò, bài hát trữ tình mang đậm bản sắc dân tộc. Những món ăn Huế được rất nhiều người biết đến và ưa chuộng như: Bún bò Huế, Bánh bèo, Bánh nậm…  ', '<p><span style="color: rgb(255, 0, 0);"><span lang="VI" style="font-weight: bold;">1. </span><span style="font-weight: bold;">Đặc điểm chương trình:</span></span></p>\r\n\r\n<h2>Xứ Huế được mệnh danh là vùng đất Kinh đô trước đây của Việt Nam với những câu hò, bài hát trữ tình mang đậm bản sắc dân tộc. Những món ăn Huế được rất nhiều người biết đến và ưa chuộng như: Bún bò Huế, Bánh bèo, Bánh nậm…</h2>\r\n\r\n<p>Trường Dạy nghề Ẩm Thực NetSpace đã sưu tầm thực đơn các món Huế được yêu thích nhất và mời các Chuyên gia Ẩm thực, Giảng viên hàng đầu phụ trách giảng dạy. Chắc chắn rằng, sau khóa học này, Học viên có thể tự mình nấu những món ăn Huế ngon và đặc sắc hoặc có thể kinh doanh những món ăn xứ Huế thành công.<br />\r\n&nbsp;</p>\r\n\r\n<p><span style="line-height: 1.6em; color: rgb(255, 0, 0);"><span style="font-weight: bold;">2. Thờ</span><span style="font-weight: bold;">i gian học</span>:</span><span style="line-height: 1.6em;"> </span><span style="line-height: 1.6em; font-weight: bold;">7 buổi&nbsp;</span></p>\r\n\r\n<p><span style="line-height: 1.6em; color: rgb(255, 0, 0);"><span style="font-weight: bold;">3. Chứng chỉ hoàn tất khóa </span><span style="font-weight: bold;">học</span>:</span><span style="line-height: 1.6em;"> do </span><span lang="VI" style="line-height: 1.6em;">Trường </span><span style="line-height: 1.6em;">Ẩm Thực NetSpace cấp có giá trị toàn quốc và Hội đầu bếp Chuyên nghiệp Sài Gòn chứng nhận.</span></p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">4. Học phí:</span></p>\r\n\r\n<p>20.000.000 đ/khóa (Đã bao gồm chi phí nguyên vật liệu thực hành, <span style="font-size: 13px; text-align: justify;">không tính chi phí nguyên liệu các buổi thi</span>).</p>\r\n\r\n<p><span style="font-weight: bold; color: rgb(255, 0, 0);">5. Nội dung khóa học</span></p>\r\n\r\n<p><span style="font-weight: bold;">Chế biến các món ăn đặc trưng Xứ Huế</span></p>\r\n\r\n<p>1. Bún bò Huế</p>\r\n\r\n<p>2. Bánh bèo</p>\r\n\r\n<p>3. Bánh nậm</p>\r\n\r\n<p>4. Bánh lọc</p>\r\n\r\n<p>5. Bánh ướt tôm cháy</p>\r\n\r\n<p>6. Bánh lá chả tôm</p>\r\n\r\n<p>7. Bánh ít ram</p>\r\n\r\n<p>8. Bánh khoái</p>\r\n\r\n<p>9. Nem chua</p>\r\n\r\n<p>10. Mít trộn tôm thịt</p>\r\n\r\n<p>11. Gỏi thập vị Cố đô</p>\r\n\r\n<p>12. Chả giò Cung đình</p>\r\n\r\n<p>13. Hến xúc bánh đa</p>\r\n\r\n<p>14. Cơm âm phủ</p>\r\n\r\n<p>15. Cơm hến</p>\r\n\r\n<p>16. Gà bóp rau râm</p>\r\n\r\n<p>17. Giò heo hon cải chua</p>\r\n\r\n<p style="text-align: center;">&nbsp;</p>\r\n', '2013-01-07 00:00:00', '2013-01-07 11:08:50', 1, 'Dạy món Huế,Day Mon Hue', 0, 1, 'vi', ',3,24,63,', 'admin', 'admin', 0);
