INSERT INTO `web_tuyendung` VALUES (1, 'Bếp chính bếp Hoa nhà hàng', 'bep-chinh-bep-hoa-nha-hang', NULL, 'Biết nấu các loại chao khac nhau. Có kinh nghiệm nấu ăn. Có kiến thức về vệ sinh an toàn thực phẩm. Có sức khỏe tốt, nhân thân tốt', 'Bếp chính bếp Hoa nhà hàng', '<p>Biết nấu các loại chao khac nhau</p>\r\n\r\n<p>Có kinh nghiệm nấu ăn</p>\r\n\r\n<p>Có kiến thức về vệ sinh an toàn thực phẩm.</p>\r\n\r\n<p>Có sức khỏe tốt, nhân thân tốt</p>\r\n', 1, 'Thỏa thuận', '3', '<p>Luong thu viec = 3.500k + an o&nbsp;<br />\r\nLuong chinh thuc = 4.000k + an o&nbsp;<br />\r\nThưởng theo năng lực và những đề xuất cải tiến<br />\r\nLàm ngày lễ được 150% lương<br />\r\nTháng lương thứ 13 ( tết âm)<br />\r\nLiên hoan hàng tháng...</p>\r\n', '<p>- Đơn xin việc<br />\r\n- Sơ yếu lý lịch.<br />\r\n- Hộ khẩu, chứng minh nhân dân và giấy khám sức khỏe.<br />\r\n- Các bằng cấp có liên quan.<br />\r\n( Lưu ý: Liên hệ trong giờ hành chính)<br />\r\n(không trả lại hồ sơ nếu không trúng tuyển)</p>\r\n', NULL, ',57,', 1, NULL, 'vi', 1, '2014-02-19 16:23:44', '2014-02-19 17:12:34', 'admin', 'admin', 0);
