INSERT INTO `info` VALUES (347, 'TUNA', 'tuna', 'tuna.jpg', '<p style="margin: 10px 0px; padding: 0px; line-height: 20px; font-family: Arial, Helvetica, sans-serif; font-size: 13px;">We care fresh tuna fish from fishing village to the overseas consumption market. With 1,500 s.qm of packing tuna area we can supply the HACCP&nbsp; processing system packing</p>\r\n', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td style="vertical-align: top;">\r\n			<h2><span style="color:#008000;">TUNA</span></h2>\r\n\r\n			<p>We care fresh tuna fish from fishing village to the overseas consumption market</p>\r\n\r\n			<p>With 1,500 s.qm of packing tuna area we can supply the HACCP&nbsp; processing system packing</p>\r\n			</td>\r\n			<td style="text-align: right;"><img alt="TUNA" src="http://hoangha.com/images/stories/picture/IMG_5198%20n.jpg" style="width: 300px; height: 200px;" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '2013-09-30 12:29:49', '2013-09-30 14:29:57', 1, 'TUNA', 0, 'en', ',5,57,', 'admin', 'admin', 0);
INSERT INTO `info` VALUES (348, 'FRESH & VEGETABLES', 'fresh-vegetables', 'fresh-vegetables.jpg', '<p>Hoang Ha offer particular special logistics services for certain circumstances. You can count on us for individualized care and attention to your shipments.</p>\r\n', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan="2">\r\n			<h2><span style="color:#008000;">FRESH &amp; VEGETABLES</span></h2>\r\n\r\n			<p>Hoang Ha offer particular special logistics services for certain circumstances. You can count on us for individualized care and attention to your shipments.</p>\r\n\r\n			<p>Processing &amp; Packing all kind of fresh fruit and vegetable</p>\r\n\r\n			<p>With the cooling warehouse system we care fruit and vegetable with high quality</p>\r\n\r\n			<p>We supply the good services of exporking documents and customs clearance</p>\r\n\r\n			<p>Nominated by international</p>\r\n\r\n			<p style="text-align: center;"><img alt="FRESH &amp; VEGETABLES" src="http://hoangha.com/images/stories/picture/quytrinh.jpg" style="width: 650px; height: 149px;" /></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '2013-09-30 12:31:26', '2013-09-29 14:03:43', 1, 'FRESH & VEGETABLES', 0, 'en', ',5,57,', 'admin', 'admin', 0);
INSERT INTO `info` VALUES (349, 'FLOWER', 'flower', 'flower.jpg', '<p>HIL&nbsp;take care your fresh beautiful flower. Some flowers are sent packed flat in boxes. This enables large amounts of flowers to be packed in small spaces like aircraft holds.</p>\r\n', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td style="vertical-align: top;">\r\n			<h2><span style="color:#008000;">FLOWER</span></h2>\r\n\r\n			<p><strong>HIL</strong> take care your fresh beautiful flower.</p>\r\n\r\n			<p>Some flowers are sent packed flat in boxes. This enables large amounts of flowers to be packed in small spaces like aircraft holds.</p>\r\n\r\n			<p>Other flowers cannot survive for long periods out of water. These are either sent with their own little water holders on each stem end.</p>\r\n			</td>\r\n			<td style="text-align: right;"><img alt="FLOWER" src="http://hoangha.com/images/stories/picture/flowers_auction.jpg" style="width: 300px; height: 265px;" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '2013-09-30 12:32:58', '2013-09-28 14:30:15', 1, 'FLOWER', 0, 'en', ',5,57,', 'admin', 'admin', 0);
INSERT INTO `info` VALUES (350, 'DIAGNOTICS', 'diagnotics', 'diagnotics.jpg', '<p>Diagnostics are items of medical equipment designed to aid in the diagnosis, monitoring or treatment of medical conditions.&nbsp;</p>\r\n', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<h2><span style="color:#008000;"><img alt="DIAGNOTICS" src="http://hoangha.com/images/stories/about_uss/Diagnotics.jpg" style="width: 300px; height: 139px; float: right; margin-left: 10px; margin-right: 10px;" />DIAGNOTICS</span></h2>\r\n\r\n			<p>Diagnostics are items of medical equipment designed to aid in the diagnosis, monitoring or treatment of medical conditions. These devices are usually designed with rigorous safety standards. Transporting this equipment requires specialist capabilities and knowledge. Generally this equipment is very sensitive and subsequently expensive. Only specialist Logistics Company''s can undertake the cargo handling of such equipment.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '2013-09-30 12:35:26', '2013-09-23 14:30:31', 1, 'DIAGNOTICS', 0, 'en', ',5,58,', 'admin', 'admin', 0);
INSERT INTO `info` VALUES (351, 'OVERSIZED/ WEIGHT CARGO', 'oversized-weight-cargo', 'oversized-weight-cargo.jpg', '<p><span style="font-family: Arial, Helvetica, sans-serif; font-size: 13px; line-height: 20px;">Oversized cargo shipping is one of our specialties has transported such unusually large air cargo as aircraft''s engine spare parts, turbines, vehicles, electric poles, automobiles.</span></p>\r\n', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="OVERSIZED/ WEIGHT CARGO" src="http://hoangha.com/images/stories/about_uss/oversized-weight.jpg" style="opacity: 0.9; width: 300px; height: 200px; float: right; margin-left: 10px; margin-right: 10px;" />\r\n			<h2><span style="color:#008000;">OVERSIZED/ WEIGHT CARGO</span></h2>\r\n\r\n			<p>Oversized cargo shipping is one of our specialties has transported such unusually large air cargo as aircraft''s engine spare parts, turbines, vehicles, electric poles, automobiles.</p>\r\n\r\n			<p>With HIL, shipping oversized air cargo is a challenge we have faced successfully for many years. We''re ready for anything you might want to ship by air, no matter how big is, long or heavy.</p>\r\n\r\n			<p>HIL''s ground personnel are among the most professional and experienced, especially in loading complex cargo of exceptional size and weight. So when it comes to oversize cargo, we''ll deliver your goods to any destination. Contact us for full info about oversized cargo.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '2013-09-30 12:37:59', '2013-09-25 14:29:47', 1, 'OVERSIZED/ WEIGHT CARGO', 0, 'en', ',5,58,', 'admin', 'admin', 0);
INSERT INTO `info` VALUES (352, 'DANGEROUS GOODS (DGR)', 'dangerous-goods-dgr', 'dangerous-goods-dgr.jpg', '<p>Being equipped more than 10 experienced employees with IATA DANGEROUS GOODS certificate, providing the excelled solution for DGR shipments.</p>\r\n', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<h2><img alt="DANGEROUS GOODS (DGR)" src="http://hoangha.com/images/stories/picture/dangrerous.jpg" style="opacity: 0.9; font-size: 13px; width: 300px; height: 274px; float: right; margin-left: 10px; margin-right: 10px;" /></h2>\r\n\r\n			<h2><span style="color: rgb(0, 128, 0); line-height: 1.2em;">DANGEROUS GOODS (DGR)</span></h2>\r\n\r\n			<p>Being equipped more than 10 experienced employees with IATA DANGEROUS GOODS certificate, providing the excelled solution for DGR shipments.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '2013-09-30 12:39:06', '2013-09-24 14:29:39', 1, 'DANGEROUS GOODS (DGR)', 0, 'en', ',5,58,', 'admin', 'admin', 0);
INSERT INTO `info` VALUES (353, 'FOOD', 'food', 'food.jpg', '<p>Long time experience in food transportation we find out:<span style="line-height: 1.6em;">&nbsp; Refrigeration can substantially reduce the rate at which food will deteriorate</span></p>\r\n', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<h2><span style="color:#008000;">FOOD</span></h2>\r\n\r\n			<p>Long time experience in food transportation we find out:</p>\r\n\r\n			<p>-&nbsp; Refrigeration can substantially reduce the rate at which food will deteriorate;</p>\r\n\r\n			<p>-&nbsp; Low temperatures slow down the growth of microorganisms and the rate of chemical (including enzymic) changes in food.</p>\r\n			</td>\r\n			<td><img alt="Food" src="http://hoangha.com/images/stories/picture/food.jpg" style="width: 300px; height: 225px;" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '2013-09-30 12:43:43', '2013-09-27 14:30:42', 1, 'FOOD', 1, 'en', ',5,57,', 'admin', 'admin', 0);
INSERT INTO `info` VALUES (354, 'PHARMACEUTICALS', 'pharmaceuticals', 'professional-cargo.jpg', '<p>Pharmaceuticals also represent a tremendous challenge for the transportation industry. With pharmaceuticals, safeguard&nbsp;can have many different meanings</p>\r\n', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<h2><img alt="​PHARMACEUTICALS" src="http://hoangha.com/images/stories/about_uss/pharmaceuticals.jpg" style="width: 300px; height: 272px; float: right; margin-left: 10px; margin-right: 10px;" /><span style="color:#008000;">PHARMACEUTICALS</span></h2>\r\n\r\n			<p>Pharmaceuticals also represent a tremendous challenge for the transportation industry. With pharmaceuticals, "safeguard" can have many different meanings. Some products are so fragile they have a lifetime of only days or weeks. Others can last far longer, but are so valuable that extraordinary security measures are absolutely essential.</p>\r\n\r\n			<p>Either way, your products have a high price tag will be demanded special treatment by HIL.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '2013-09-30 12:46:44', '2013-09-22 14:03:00', 1, 'PHARMACEUTICALS', 1, 'en', ',5,58,', 'admin', 'admin', 0);
INSERT INTO `info` VALUES (355, 'PET', 'pet', 'pet.jpg', '<p>Transport services for Live animal, Pet &amp; Tropical fish are provided by our experienced team of transportation professionals.</p>\r\n', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<h2><span style="color:#008000;"><img alt="PET" src="http://hoangha.com/images/stories/picture/Flock_of_sheep.jpg" style="width: 300px; height: 357px; float: right; margin-left: 10px; margin-right: 10px;" />PET</span></h2>\r\n\r\n			<p>Transport services for Live animal, Pet &amp; Tropical fish are provided by our experienced team of transportation professionals.</p>\r\n\r\n			<p>With excellent HIL''s services, your lovely Pet will be moved by the utmost priority and care.</p>\r\n\r\n			<p>Trust our personal services for safe and comfortable transport of your live animals throughout the world.</p>\r\n\r\n			<p>It doesn''t matter if you''re shipping dogs, cats, tropical fish or even live sheep. If they can safely and comfortably fit in our services. To consider them as our personal very important pets to fly first class all the way to their destination.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', '2013-09-30 12:48:49', '2013-09-21 09:31:09', 1, 'PET', 1, 'en', ',5,59,', 'admin', 'admin', 0);
INSERT INTO `info` VALUES (356, 'TROPICAL FISH', 'tropical-fish', 'tropical-fish.jpg', '<p>Transport services for Live animal, Pet &amp; Tropical fish are provided by our experienced team of transportation professionals.</p>\r\n', '<table border="0" cellpadding="0" cellspacing="10" style="width: 100%;">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<h2><span style="color:#008000;"><img alt="​TROPICAL FISH" src="http://hoangha.com/images/stories/picture/fish.jpg" style="width: 300px; height: 235px; float: right; margin-left: 10px; margin-right: 10px;" />TROPICAL FISH</span></h2>\r\n\r\n			<p>Transport services for Live animal, Pet &amp; Tropical fish are provided by our experienced team of transportation professionals.</p>\r\n\r\n			<p>With excellent HIL''s services, your lovely Pet will be moved by the utmost priority and care.</p>\r\n\r\n			<p>Trust our personal services for safe and comfortable transport of your live animals throughout the world.</p>\r\n\r\n			<p>It doesn''t matter if you''re shipping dogs, cats, tropical fish or even live sheep. If they can safely and comfortably fit in our services. To consider them as our personal very important pets to fly first class all the way to their destination.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', '2013-09-30 12:49:43', '2013-09-20 14:03:08', 1, 'TROPICAL FISH', 1, 'en', ',5,59,', 'admin', 'admin', 0);
INSERT INTO `info` VALUES (357, 'Express', 'express', '', '<p>HIL will always take care and concern in delivering your merchandise . We have local and international deliveries services.</p>\r\n', '<h2><span style="line-height: 1.6em;">HIL will always take care and concern in delivering your merchandise . We have local and international deliveries services.</span></h2>\r\n\r\n<p><span style="line-height: 1.6em;">We will get packages and shipments to their destination safely and just-in-time . Customer service is always number #1 at HIL. Your problems will be resolve by our hard working and experience</span></p>\r\n', '2013-09-30 15:08:48', '2013-09-30 15:08:48', 1, 'Express', 0, 'en', ',12,', 'admin', NULL, 0);
INSERT INTO `info` VALUES (358, 'Chuyển phát nhanh', 'chuyen-phat-nhanh', '', '<p>HIL luôn chăm sóc và quan tâm đến việc vận chuyển hàng hóa của bạn. Chúng tôi có dịch vụ giao hàng tại địa phương và quốc tế.</p>\r\n', '<h2>HIL luôn chăm sóc và quan tâm đến việc vận chuyển hàng hóa của bạn. Chúng tôi có dịch vụ giao hàng tại địa phương và quốc tế.</h2>\r\n\r\n<p>Chúng tôi sẽ nhận kiện hàng và vận chuyển chúng đến đích một cách an toàn và đúng lúc. Dịch vụ khách hàng luôn luôn là số 1 tại HIL. Vấn đề của bạn sẽ được giải quyết bằng cách làm việc chăm chỉ và kinh nghiệm của chúng tôi</p>\r\n', '2013-09-30 15:10:12', '2013-09-30 15:10:12', 1, 'Chuyển phát nhanh', 0, 'vi', ',21,', 'admin', NULL, 0);
INSERT INTO `info` VALUES (359, 'Chuỗi cung ứng', 'chuoi-cung-ung', 'supply-chain.jpg', '<p>Tại HIL, các chuyên gia chuỗi cung ứng giúp khách hàng đạt được tiết kiệm tổng thể chuỗi cung ứng, trong khi giành quyền kiểm soát tốt hơn các lô hàng của họ.</p>\r\n', '<h2>Tại HIL, các chuyên gia chuỗi cung ứng giúp khách hàng đạt được tiết kiệm tổng thể chuỗi cung ứng, trong khi giành quyền kiểm soát tốt hơn các lô hàng của họ.</h2>\r\n\r\n<p>Đội ngũ của chúng tôi có thể phối hợp và tối ưu hóa các yếu tố hậu cần, tài chính và pháp lý của chuỗi cung ứng toàn cầu của bạn.</p>\r\n', '2013-09-30 15:16:19', '2013-09-30 15:16:19', 1, 'Chuỗi cung ứng', 1, 'vi', ',20,', 'admin', NULL, 0);
INSERT INTO `info` VALUES (360, 'Môi giới hải quan', 'moi-gioi-hai-quan', 'customs-broker.jpg', '<p>Với kinh nghiệm, và giấy phép môi giới hải quan Hoàng Hà cung cấp cho khách hàng các dịch vụ hải quan phức tạp và các giải pháp chuyên nghiệp phù hợp với yêu cầu logistics cụ thể của họ.</p>\r\n', '<h2>Với kinh nghiệm, và giấy phép môi giới hải quan Hoàng Hà cung cấp cho khách hàng các dịch vụ hải quan phức tạp và các giải pháp chuyên nghiệp phù hợp với yêu cầu logistics cụ thể của họ.</h2>\r\n\r\n<p>Chúng tôi cung cấp dịch vụ môi giới hải quan nhanh chóng, đáng tin cậy và chính xác cho các lô hàng nhập khẩu và xuất khẩu</p>\r\n', '2013-09-30 15:18:14', '2013-09-30 15:18:14', 1, 'Môi giới hải quan', 1, 'vi', ',20,', 'admin', NULL, 0);
INSERT INTO `info` VALUES (361, 'Chuyển phát nhanh quốc tế', 'chuyen-phat-nhanh-quoc-te', 'door-services.jpg', '<p>HIL cung cấp một giải pháp toàn cầu cho việc chuyển phát nhanh tài liệu và bưu kiện tới tận nhà trên toàn thế giới và trong một loạt các tùy chọn thời gian vận chuyển sẽ đáp ứng mọi nhu cầu của bạn.</p>\r\n', '<h2>HIL cung cấp một giải pháp toàn cầu cho việc chuyển phát nhanh tài liệu và bưu kiện tới tận nhà trên toàn thế giới và trong một loạt các tùy chọn thời gian vận chuyển sẽ đáp ứng mọi nhu cầu của bạn.</h2>\r\n\r\n<p>Dịch vụ chuyển phát nhanh quốc tế của chúng tôi cung cấp cho bạn thông tin thời gian theo dõi, Bằng chứng giao hàng của lô hàng của bạn, và khả năng để yêu cầu thông báo giao hàng qua email hoặc tin nhắn SMS.</p>\r\n', '2013-09-30 15:19:50', '2013-09-30 15:19:50', 1, 'Chuyển phát nhanh quốc tế', 1, 'vi', ',20,', 'admin', NULL, 0);
INSERT INTO `info` VALUES (362, 'Đóng gói thủy sản', 'dong-goi-thuy-san', 'packing-services.jpg', '<p>HIL cung cấp đầy đủ các loại bao bì đặc biệt cho việc đóng gói mặt hàng thủy sản như cá ngừ tươi, cua sống, cá tươi, v.v..</p>\r\n', '<h2>HIL cung cấp đầy đủ các loại bao bì đặc biệt cho việc đóng gói mặt hàng thủy sản như cá ngừ tươi, cua sống, cá tươi, v.v..</h2>\r\n\r\n<p>Với đội ngũ công nhận chuyên nghiệp và được đào tạo, chúng tôi có thể giúp bạn nhiều hoặc ít đối với nhu cầu đóng gói của bạn cũng như trong các dịch vụ giá trị gia tăng. Chúng tôi cũng bán thùng và nhận đóng gói.</p>\r\n', '2013-09-30 15:21:50', '2013-09-30 17:00:00', 1, 'Đóng gói thủy sản', 1, 'vi', ',20,', 'admin', 'admin', 0);
