(103, 'Bột mì con cừu', 'bot-mi-con-cuu', 'bot-mi-con-cuu.jpg', 'Bột mì con cừu chuyên làm bánh mì đặc ruột, bánh mì thơm ngon.', '', '2013-10-07 11:17:02', '2013-02-22 11:18:10', 1, 'Bột mì con cừu', '<p><strong>Bột mì con cừu chuyên làm bánh mì đặc ruột, b<span style="line-height: 1.6em;">ánh mì thơm ngon.</span></strong></p>\r\n\r\n<p><span style="line-height: 1.6em;">Khối lượng: 40kg</span></p>\r\n', 0, 1, 'vi', ',10,25,27,', 'admin', 'admin', 0),
(104, 'Bột mì đa dụng hiệu Táo đỏ', 'bot-mi-da-dung-hieu-tao-do', 'bot-mi-da-dung.jpg', 'Bột mì đa dụng hiệu Táo đỏ rất phù hợp để chế biến các món hải sản tẩm bột, gà tẩm bột, chế biến các loại bánh: Bánh mì, bánh bao, bánh ngọt và các loại bánh khác', '<p><strong>Sử dụng:</strong></p>\r\n\r\n<h2>-&nbsp;Bột mì đa dụng hiệu Táo đỏ rất phù hợp để chế biến các món hải sản tẩm bột, gà tẩm bột, chế biến các loại bánh: Bánh mì, bánh bao, bánh ngọt và các loại bánh khác, ĐẶC BIỆT BÁNH BÔNG LAN.</h2>\r\n\r\n<p>-&nbsp;Bột mì được đóng gói 1 kg đảm bảo vệ sinh, an toàn thực phẩm, sử dụng tiện lợi trong gia đình.</p>\r\n\r\n<p style="text-align: center;"><strong>Thành phần dinh dưỡng</strong></p>\r\n\r\n<p style="text-align: center;"><em>Nutrition Facts</em></p>\r\n\r\n<p style="text-align: center;"><em>(Tính trên100g bột khô)</em></p>\r\n\r\n<p>Năng lượng&nbsp;(Calorie): 357 Kcal</p>\r\n\r\n<p>Đạm (Protein): &gt; 9</p>\r\n\r\n<p>Chất béo&nbsp;(Fat): &lt; 2</p>\r\n\r\n<p>Carbohydrate: &gt; 70</p>\r\n\r\n<p>Natri: 26,6</p>\r\n\r\n<p><strong>Chỉ tiêu chất lượng chủ yếu:&nbsp;</strong> Protein &gt; 9%</p>\r\n', '2013-10-23 08:47:35', '2013-10-23 08:54:31', 1, 'Bột mì đa dụng hiệu Táo đỏ', '<p style="color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, ''Trebuchet MS''; font-size: 13px; line-height: 20.796875px;"><strong>Khối lượng tịnh:</strong> 1kg</p>\r\n\r\n<p style="color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, ''Trebuchet MS''; font-size: 13px; line-height: 20.796875px;"><strong>Thành phần:</strong> Lúa mì</p>\r\n\r\n<p style="color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, ''Trebuchet MS''; font-size: 13px; line-height: 20.796875px;"><strong>Bột mì đa dụng cao cấp</strong></p>\r\n\r\n<p style="color: rgb(51, 51, 51); font-family: sans-serif, Arial, Verdana, ''Trebuchet MS''; font-size: 13px; line-height: 20.796875px;"><strong>Bảo quản nơi khô ráo, thoáng mát.</strong></p>\r\n', 0, 0, 'vi', ',10,25,27,', 'admin', 'admin', 0),
(77, 'Bột vỏ bánh Mặt Trời 1kg', 'bot-vo-banh-mat-troi', 'bot-banh-mat-troi.jpg', 'Bột Bánh Mặt Trời cho bánh thơm ngon, bánh nở phồng to, tách lớp, màu vàng bóng', '<p><strong style="line-height: 1.6em;">Ph&acirc;n phối</strong><span style="line-height: 1.6em;">:&nbsp;</span><em style="line-height: 1.6em;">Sản phẩm hiện c&oacute; b&aacute;n tại tất cả hệ thống si&ecirc;u thị trong cả nước, cửa h&agrave;ng VIKYFOOD v&agrave; c&aacute;c đại l&yacute; của C&ocirc;ng ty. Cần hỗ trợ th&ecirc;m th&ocirc;ng tin, vui l&ograve;ng li&ecirc;n hệ Bộ phận Tư Vấn &amp; Chăm S&oacute;c Kh&aacute;ch H&agrave;ng qua số điện thoại 08 3854 7777.</em></p>\r\n', '2013-03-09 03:41:44', '2013-02-01 05:18:41', 1, 'Bột vỏ bánh Mặt Trời 1kg', '<p style="font-size: 13px;"><strong>Sản phẩm d&ugrave;ng để chi&ecirc;n.</strong></p>\r\n\r\n<p style="font-size: 13px;"><strong>M&atilde; SP:&nbsp;</strong>VMT482</p>\r\n\r\n<p style="font-size: 13px;"><strong>Đặc t&iacute;nh</strong>: Bột vỏ b&aacute;nh Mặt Trời cho b&aacute;nh thơm ngon, b&aacute;nh nở phồng to, t&aacute;ch lớp, m&agrave;u v&agrave;ng b&oacute;ng.</p>\r\n\r\n<p style="font-size: 13px;"><strong>Bảo quản</strong>: Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t</p>\r\n', 34000, 0, 'vi', ',10,26,82,', 'admin', 'admin', 0),
